key10001001={10001001,20001,1,"凤出琅琊",1,1,0,0,2,3,4,6,1231,"梅长苏踏下琅琊山，准备金陵之行，一了夙愿。",653,1,902,"1009001,1;1009002,1;2,1000;1014001,10;1012001,5;1,50","1,10;2,360","推荐英雄等级：1~2\n普通的敌人。"},
key10001002={10001002,20001,2,"歇雨崖边",1,1,0,10001001,2,3,4,6,1232,"半山歇雨崖，遇见来迎接的金陵二少，言豫津和萧景睿。",656,1,423,"1018001,10;1014001,10;1012001,5;1,50","1,10;2,360","推荐英雄等级：1~2\n普通的敌人。"},
key10001003={10001003,20001,3,"叹海楼前",2,1,0,10001002,2,3,4,6,1233,"三人同行，途径叹海楼，见到被峭龙帮追杀的卓青遥。",1580,1,1129,"1012001,6;2,1000;1014001,10;1,50","1,10;2,460","推荐英雄等级：2~3\n精英，擅于内功攻击最前排目标。"},
key10001004={10001004,20001,4,"擎天一束",3,1,0,10001003,2,3,4,6,1234,"峭龙帮帮主束擎天被迫露面，江湖人也并非自在高远。",658,1,415,"8000077,10;1012001,6;2,1000;1014001,10;1,50","1,10;2,540","推荐英雄等级：2~3\n精英，擅于内功攻击竖列目标。"},
key10002001={10002001,20002,1,"金陵偶遇",1,1,0,10001004,1,2,4,6,1221,"苏哲三人入金陵，城门遇霓凰，风起车帘，却见故人飒爽风姿尤胜当年。",657,1,798,"1012001,6;2,10000;1010001,10;1014001,10;1,50;1018001,10","1,10;2,360","推荐英雄等级：4~5\n普通的敌人。"},
key10002002={10002002,20002,2,"比武招亲",1,1,0,10002001,1,2,4,6,1222,"霓凰掌军得权呼声高，帝王忌惮，借其年龄渐长为由举办比武招亲。",653,1,435,"1012001,6;2,1000;1014001,10;1,50;1018001,10","1,10;2,540","推荐英雄等级：4~5\n普通的敌人。"},
key10002003={10002003,20002,3,"风云渐起",2,1,0,10002002,1,2,4,6,1223,"得霓凰者即得虎符，兵权诱惑，报名者涉及各国势力，搅起金陵暗涌。",1580,1,1489,"1012001,6;2,1000;1014001,10;1,50;1018001,10","1,10;2,600","推荐英雄等级：7~8\n精英，擅于内功攻击全体目标。"},
key10002004={10002004,20002,4,"龙蛇混杂",3,1,0,10002003,1,2,4,6,1224,"比武招亲参赛者良莠不齐，豫津景睿报名参赛，意为穆姐姐淘汰鱼目。",658,1,1725,"1012001,6;2,1000;1014001,10;1,50;1018001,10","1,10;2,640","推荐英雄等级：8~9\nBOSS，擅于外功攻击单体目标。"},
key10002005={10002005,20003,1,"麒麟才子",1,1,0,10002004,0,1,4,6,1231,"誉王一掷千金得锦囊：麒麟才子，江左梅郎，得之，可得天下，",654,1,1114,"1012001,6;2,1000;1014001,10;1,50;1018001,10","1,10;2,820","推荐英雄等级：10~11\n普通的敌人。"},
key10002006={10002006,20003,2,"东宫太子",1,1,0,10002005,0,1,4,6,1232,"太子得消息，知麒麟之才，愤怒誉王狼子野心，决意招揽梅长苏。",657,1,421,"1012001,6;2,1000;1014001,10;1,50;1018002,20","1,10;2,720","推荐英雄等级：10~11\n普通的敌人。"},
key10002007={10002007,20003,3,"争相招揽",2,1,0,10002006,0,1,4,6,1233,"太子誉王迎凤楼偶遇苏哲，争相讨好，苏哲好意均受然并不答复。",1580,1,756,"1012001,6;2,1000;1014001,10;1,50;1018002,20","1,10;2,760","推荐英雄等级：10~11\n精英，擅于内功攻击全体目标。"},
key10002008={10002008,20003,4,"双龙夺嫡",3,1,0,10002007,0,1,4,6,1234,"麒麟才子消息不胫而走，夺嫡由暗转明，自此，梅长苏卷入金陵风波。",658,1,1489,"2,8000;2,1000;1014001,10;1012001,5;1,50;1018002,20","1,10;2,780","推荐英雄等级：10~11\nBOSS，擅于内功攻击最前排目标。"},
key10001011={10001011,10001,1,"太皇太后",1,1,0,10002008,0,1,4,6,1121,"太皇太后约见晚辈，人老心明，识得林殊，叠握霓凰小殊双手祝福。",657,1,1110,"1014001,10;2,1000;1012001,5;1,50","1,10;2,780","推荐英雄等级：11~12\n普通的敌人。"},
key10001013={10001013,10001,2,"霓凰相约",1,1,0,10001011,0,1,4,6,1122,"林殊不舍太皇太后，眼中泄露真情意，霓凰起疑，约苏先生相谈。",655,1,798,"2,20000;1014001,10;1012001,5;1,50","1,10;2,820","推荐英雄等级：11~12\n普通的敌人。"},
key10001014={10001014,10001,3,"何为有缘人",2,1,0,10001013,0,1,4,6,1123,"霓凰试探，询问苏哲何为有缘人，直视之下苏哲苦笑怔住。",1580,1,1488,"1014001,10;1012001,5;1,50","1,10;2,820","推荐英雄等级：12~13\n精英，对全体目标施加持续毒伤害。"},
key10001012={10001012,10001,4,"有缘自在心",3,1,0,10001014,0,1,4,6,1124,"苏哲怕泄漏情绪，不敢直视霓凰，答曰缘分自在郡主心中。",658,1,798,"1014001,10;2,1000;1012001,5;1,50","1,10;2,840","推荐英雄等级：12~13\nBOSS，擅于外功攻击单体目标。"},
key10001021={10001021,10002,1,"择主何人",1,1,0,10001012,0,1,4,6,1231,"霓凰放下苏哲是故人的怀疑，询问其欲择主何人。",652,1,902,"1014001,10;2,1000;1012001,5;1,20","1,100;2,840","推荐英雄等级：15~16\n普通的敌人。"},
key10001022={10001022,10002,2,"罪奴小庭生",1,1,0,10001021,0,1,4,6,1232,"苏哲并未给出确切答复，两人行至后庭，遇太监鞭笞掖庭罪奴庭生。",653,1,417,"1014001,10;2,1000;1012001,5;1,20","1,100;2,840","推荐英雄等级：15~16\n普通的敌人。"},
key10001023={10001023,10002,3,"靖王与庭生",2,1,0,10001022,0,1,4,6,1233,"太监言语之中透露靖王竟十分照拂小庭生，苏哲起疑与霓凰保护庭生。",1580,1,792,"1014001,10;2,1000;1012001,5;1,20","1,100;2,860","推荐英雄等级：16~17\n精英，擅于外功攻击单体目标。"},
key10001024={10001024,10002,4,"许诺救庭生",3,1,0,10001023,0,1,4,6,1234,"靖王赶来，欲救庭生然力所难及，苏哲沉思，许下诺言有办法救出庭生。",658,1,793,"1014001,10;1012001,5;1,20","1,100;2,860","推荐英雄等级：16~17\nBOSS，擅于内功攻击竖列目标。"},
key10001031={10001031,10003,1,"豫津参赛",1,1,0,10001024,0,1,4,6,1221,"比武招亲继续进行，言豫津将要参赛，兴奋之下力邀众人观战。",654,1,430,"1014001,10;1012001,5;1,20","1,100;2,860","推荐英雄等级：17~18\n普通的敌人。"},
key10001032={10001032,10003,2,"正当年少",1,1,0,10001031,0,1,4,6,1222,"第一场比试豫津轻松过关，年轻人的朝气蓬勃令苏哲驻足。",653,1,902,"1014001,10;1012001,5;1,20","1,100;2,980","推荐英雄等级：17~18\n普通的敌人。"},
key10001034={10001034,10003,3,"高手夏无眠",2,1,0,10001032,0,1,4,6,1223,"得知新对手是江湖高手夏无眠，一手剑法细密强劲，豫津劲头十足。",1580,1,420,"1014001,10;1012001,5;1,20","1,100;2,1000","推荐英雄等级：18~19\n精英，擅于内功攻击全体敌人。"},
key10001033={10001033,10003,4,"落英缤纷",3,1,0,10001034,0,1,4,6,1224,"个性张扬的豫津一招，落英缤纷，舞得十分精彩，漂亮赢下一局。",658,1,481,"1014001,10;1012001,5;1,20","1,100;2,1000","推荐英雄等级：19~20\nBOSS，擅于内功攻击全体敌人。"},
key10001041={10001041,10004,1,"北燕百里奇",1,1,0,10001033,0,1,4,6,1121,"比武场现奇人，北燕，百里奇，一招打败青年才俊秦尚志引骚动。",652,1,1129,"1014001,10;1012001,5;1,20","1,100;2,1000","推荐英雄等级：20~21\n普通的敌人。"},
key10001043={10001043,10004,2,"霓凰相约",1,1,0,10001041,0,1,4,6,1122,"梁帝担忧赛果，霓凰潜意识只有梅长苏有办法解决百里奇，相约见面。",654,1,902,"1014001,10;1012001,5;1,20","1,100;2,1000","推荐英雄等级：20~21\n普通的敌人。"},
key10001044={10001044,10004,3,"幼子打法",2,1,0,10001043,0,1,4,6,1123,"梅长苏谈及百里奇，称打败其并非不可，提出可教幼子阵法将其战胜。",1580,1,438,"1014001,10;1012001,5;1,20","1,100;2,1020","推荐英雄等级：21~22\n精英，擅于内功攻击全体敌人。"},
key10001046={10001046,10004,4,"霓凰相助",3,1,0,10001044,0,1,4,6,1124,"霓凰隐约猜到梅长苏欲借此事救出庭生，便向梁帝提出此方法。",658,1,1487,"1014001,10;1012001,5;1,20","1,100;2,1020","推荐英雄等级：21~22\nBOSS，擅于内功攻击最前排目标。"},
key10001051={10001051,10005,1,"夏无眠不忿",1,1,0,10001046,0,1,4,6,1231,"夏无眠被言豫津打败，并且输得难看，心生不忿，欲夺回声誉。",657,1,420,"1014001,10;1012001,5;1,20","1,100;2,1020","推荐英雄等级：22~23\n普通的敌人。"},
key10001053={10001053,10005,2,"兄弟夏生愁",1,1,0,10001051,0,1,4,6,1232,"兄弟同心，兄长夏生愁为弟报仇，参赛比武招亲。",657,1,481,"1014001,10;1012001,5;1,20","1,100;2,1020","推荐英雄等级：22~23\n普通的敌人。"},
key10001054={10001054,10005,3,"毒辣伤人",2,1,0,10001053,0,1,4,6,1233,"夏生愁手法毒辣，意不在胜，却在伤人，引起公愤。",1580,1,481,"1014001,10;1012001,5;1,20","1,100;2,1040","推荐英雄等级：23~24\n精英，擅于内功攻击竖列目标。"},
key10001056={10001056,10005,4,"心服口服",3,1,0,10001054,0,1,4,6,1234,"梅长苏提议出奇制胜，终是打败夏生愁，令其心服口服。",658,1,420,"1014001,10;1012001,5;1,20","1,100;2,1040","推荐英雄等级：23~24\nBOSS，擅于内功攻击竖列目标。"},
key10001061={10001061,10006,1,"庭生入选",1,1,0,10001056,0,1,4,6,1221,"梁帝同意幼子方案，将掖庭幼子带至大殿供选，梅长苏借机择选庭生。",655,1,417,"1014001,10;1012001,5;1,20","1,100;2,1040","推荐英雄等级：24~25\n普通的敌人。"},
key10001063={10001063,10006,2,"速成阵法",1,1,0,10001061,0,1,4,6,1222,"梅长苏令飞流教导小儿速成阵法，颇有小成，大将军蒙挚赞不绝口。",653,1,69,"1014001,10;1012001,5;1,20","1,100;2,1040","推荐英雄等级：24~25\n普通的敌人。"},
key10001064={10001064,10006,3,"一决胜负",2,1,0,10001063,0,1,4,6,1223,"终于到了决一胜负的时刻，小儿们不负众望，果然打败百里奇。",1580,1,1129,"1014001,10;1012001,5;1,20","1,100;2,1060","推荐英雄等级：25~26\n精英，擅于内功攻击最前排目标。"},
key10001066={10001066,10006,4,"庭生获救",3,1,0,10001064,0,1,4,6,1224,"梁帝大喜，梅长苏霓凰趁机提出奖励罪奴，梁帝终松口放出庭生。",658,1,1489,"1014001,10;1012001,5;1,20","1,100;2,1060","推荐英雄等级：25~26\nBOSS，擅于内功攻击竖列目标。"},
key10001071={10001071,10007,1,"名声大噪",1,1,0,10001066,0,1,4,6,1231,"梅长苏名声大噪，太子誉王再是按耐不住，争相招揽。",657,1,902,"1014001,10;1012001,5;1,20","1,100;2,1060","推荐英雄等级：25~26\n普通的敌人。"},
key10001073={10001073,10007,2,"投誉王门下",1,1,0,10001071,0,1,4,6,1232,"梅长苏假意投誉王门下，太子怒，求之不得便欲毁之杀之。",652,1,421,"1014001,10;1012001,5;1,20","1,100;2,1060","推荐英雄等级：25~26\n普通的敌人。"},
key10001074={10001074,10007,3,"夜黑风高",2,1,0,10001073,0,1,4,6,1233,"梅长苏回雪庐，夜黑风高杀人夜，神秘杀手诡异出现，欲夺其性命。",1580,1,775,"1014001,10;1012001,5;1,20","1,100;2,1060","推荐英雄等级：26~27\n精英，擅于内功攻击单体目标。"},
key10001076={10001076,10007,4,"黑衣杀手",3,1,0,10001074,0,1,4,6,1234,"飞流奋力抵抗杀手，然而敌众我寡，梅长苏遇危，形势并不乐观。",658,1,775,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：26~27\nBOSS，擅于内功攻击最前排目标。"},
key10001081={10001081,10008,1,"飞流的武功",1,1,0,10001076,0,1,4,6,1221,"其行如风，飞流身形诡异，捉摸不透，杀手终不敌倒下。",652,1,398,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：26~27\n普通的敌人。"},
key10001083={10001083,10008,2,"谢玉府兵",1,1,0,10001081,0,1,4,6,1222,"细丝小结是真相，梅长苏找出杀手线索，竟是谢玉府兵，不禁微笑。",652,1,424,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：26~27\n普通的敌人。"},
key10001084={10001084,10008,3,"心如明镜",2,1,0,10001083,0,1,4,6,1223,"心中明镜，谢玉，你终于露出狐狸尾巴，这次刺杀也会有完美解释吧。",1580,1,71,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\n精英，擅于外功突击后排单体目标。"},
key10001086={10001086,10008,4,"切磋比试",3,1,0,10001084,0,1,4,6,1224,"二人回谢府，谢玉见二人回来不露惊诧，只解释为府兵与飞流比试。",658,1,71,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\nBOSS，擅于外功突击后排单体目标。"},
key10001091={10001091,10009,1,"心如深海",1,1,0,10001086,0,1,4,6,8101,"梅长苏淡笑不语，飞流冷眼相对，眼望两人伫立门前，谢玉心下纷乱。",657,1,424,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key11001092={11001092,10009,2,"雪庐来客",1,1,0,10001091,0,1,4,6,8102,"二人回到雪庐，飞流呛声询问，什么人？声起影随，人已攻出。",652,1,398,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key10001093={10001093,10009,3,"剑影纠缠",1,1,0,11001092,0,1,4,6,8103,"眨眼间，隐秘处飞出四五个黑影，均与飞流剑影纠缠。",653,1,1782,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\n普通的敌人。"},
key11001094={11001094,10009,4,"来者何人",2,1,0,10001093,0,1,4,6,8104,"梅长苏驻足观望，来人不算多，手法并不狠辣，招招仅为自卫。",1580,1,1490,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：27~28\n精英，擅于内功攻击单体目标。"},
key10001095={10001095,10009,5,"心有答案",1,1,0,11001094,0,1,4,6,8105,"心中有答案，梅长苏喊住飞流，飞流不要打了，飞流听话停下。",654,1,398,"1014001,10;1012001,5;1,20","1,100;2,1080","推荐英雄等级：28~29\n普通的敌人。"},
key11001097={11001097,10009,6,"莅阳长公主",1,1,0,10001095,0,1,4,6,8106,"望向密林，梅长苏微笑，深夜来访必有急事，长公主不如进来说话。",655,1,902,"1014001,10;1012001,5;1,20","1,100;2,1100","推荐英雄等级：28~29\n普通的敌人。"},
key11001098={11001098,10009,7,"有事相求",1,1,0,11001097,0,1,4,6,8107,"果不其然，密林徐徐走出一人，真是谢玉之妻，一脸焦急的莅阳。",656,1,400,"1014001,10;1012001,5;1,20","1,100;2,1100","推荐英雄等级：28~29\n普通的敌人。"},
key10001096={10001096,10009,8,"情丝绕",3,1,0,11001098,0,1,4,6,8108,"莅阳告知宫中又现，情丝绕，应是针对霓凰，请求梅长苏相助。",658,1,1490,"1014001,10;1012001,5;1,20","1,100;2,1100","推荐英雄等级：28~29\nBOSS，擅于内功攻击竖列目标。"},
key10001101={10001101,10010,1,"心思百转",1,1,0,10001096,0,1,4,6,8201,"得知何为，情丝绕，心思急转猜到宫中有人欲用肮脏手段陷害霓凰。",654,1,902,"1014001,10;1012001,5;1,20","1,100;2,1100","推荐英雄等级：29~30\n普通的敌人。"},
key10001102={10001102,10010,2,"急血攻心",1,1,0,10001101,0,1,4,6,8202,"梅长苏心下焦虑，呕出一口攻心急血，飞流担忧不已，急寻蒙挚。",655,1,69,"1014001,10;1012001,5;1,20","1,100;2,1100","推荐英雄等级：29~30\n普通的敌人。"},
key10001103={10001103,10010,3,"金陵巨盗",1,1,0,10001102,0,1,4,6,8203,"然而蒙挚并不在营中，被告知正带弟兄去缉拿金陵巨盗唐氏姐妹。",652,1,797,"1014001,10;1012001,5;1,20","1,100;2,1120","推荐英雄等级：29~30\n普通的敌人。"},
key11001104={11001104,10010,4,"唐氏姐妹",2,1,0,10001103,0,1,4,6,8204,"唐氏姐妹。身手矫健，一式轻功形如鬼魅，多盗大户官宦之家。",1580,1,1782,"1014001,10;1012001,5;1,20","1,100;2,1120","推荐英雄等级：29~30\n精英，擅于对目标施加持续毒伤害。"},
key10001105={10001105,10010,5,"巨盗动向",1,1,0,11001104,0,1,4,6,8205,"梅长苏根据线索，猜测出唐氏姐妹动向，让飞流速找人通知蒙挚。",653,1,69,"1014001,10;1012001,5;1,20","1,100;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key11001106={11001106,10010,6,"巨富金家",1,1,0,10001105,0,1,4,6,8206,"寻到蒙挚，梅长苏将想法提出，唐氏姐妹的目标应是巨富金家。",656,1,1488,"1014001,10;1012001,5;1,20","1,100;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key11001108={11001108,10010,7,"天罗地网",1,1,0,11001106,0,1,4,6,8207,"蒙挚按照梅长苏提供的线索，布下天罗地网，果然擒住两姐妹。",657,1,797,"1014001,10;1012001,5;1,20","1,100;2,1120","推荐英雄等级：30~31\n普通的敌人。"},
key10001107={10001107,10010,8,"蒙挚会和",3,1,0,11001108,0,1,4,6,8208,"金陵巨盗案终于告破，蒙挚速速脱身与梅长苏会和。",658,1,1782,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：30~31\nBOSS，擅于对目标施加持续毒伤害。"},
key10001111={10001111,10011,1,"小心皇后",1,1,0,10001107,0,1,4,6,8301,"蒙挚寻到梅长苏，得知梅长苏已提醒进宫的霓凰，小心皇后。",653,1,69,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key10001112={10001112,10011,2,"幕后主使",1,1,0,10001111,0,1,4,6,8302,"梅长苏猜测，召霓凰进宫的后宫之人，应就是情丝绕的幕后主使。",652,1,73,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key10001113={10001113,10011,3,"猜错了",1,1,0,10001112,0,1,4,6,8303,"却得知誉王今日并未入宫，梅长苏猛然发觉设计霓凰的凶手不是皇后。",656,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：31~32\n普通的敌人。"},
key11001114={11001114,10011,4,"晚了一步",2,1,0,10001113,0,1,4,6,8304,"蒙挚急急入宫寻霓凰，然而还是晚了一步，霓凰已被越贵妃召走。",1580,1,1782,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：31~32\n精英，擅于内功攻击单体目标。"},
key10001115={10001115,10011,5,"打听消息",1,1,0,11001114,0,1,4,6,8305,"武将无法入后宫，蒙挚无可奈何，只得派出心腹继续打听。",655,1,69,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：32~33\n普通的敌人。"},
key11001116={11001116,10011,6,"霓凰有危",1,1,0,10001115,0,1,4,6,8306,"蒙挚将宫中消息告知梅长苏，两人都明白，霓凰必定十分危险。",654,1,902,"1014001,10;1012001,5;1,20","1,100;2,1140","推荐英雄等级：32~33\n普通的敌人。"},
key11001118={11001118,10011,7,"四管齐下",1,1,0,11001116,0,1,4,6,8307,"梅长苏想出无可奈何之法，需要靖王、誉王、皇后、穆青四管齐下。",657,1,66,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：32~33\n普通的敌人。"},
key10001117={10001117,10011,8,"求人相助",3,1,0,11001118,0,1,4,6,8308,"当下蒙挚去寻皇上，梅长苏求得靖王提前帮手，并告知穆青守宫禁。",658,1,774,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：32~33\nBOSS，擅于对目标施加持续毒伤害。"},
key10001121={10001121,10012,1,"静心求法",1,1,0,10001117,0,1,4,6,8401,"靖王在宫中并没有势力，梅长苏知道他应对不了太久，心下急转。",657,1,902,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key10001122={10001122,10012,2,"低头求助",1,1,0,10001121,0,1,4,6,8402,"要救霓凰，蒙挚虽可帮手，却不可出面，梅长苏知道只能向誉王低头。",656,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key10001123={10001123,10012,3,"誉王府",1,1,0,10001122,0,1,4,6,8403,"太子得霓凰，最受损害的即是誉王，梅长苏苦笑，只身前往誉王府。",655,1,902,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：33~34\n普通的敌人。"},
key11001124={11001124,10012,4,"小鬼最难缠",2,1,0,10001123,0,1,4,6,8404,"俗话说得好，阎王好说话，小鬼最难缠，没见着誉王，却……",1580,1,1725,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：33~34\n精英，擅于内功攻击竖列目标。"},
key10001125={10001125,10012,5,"狗腿灰鹞",1,1,0,11001124,0,1,4,6,8405,"得知来人是梅长苏，誉王府家卫，狗腿灰鹞嘲当下冷脸嘲讽。",654,1,1726,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key11001126={11001126,10012,6,"求见誉王",1,1,0,10001125,0,1,4,6,8406,"梅长苏面色未动，按住飞流，无视灰鹞继续求见誉王。",653,1,902,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key11001128={11001128,10012,7,"强闯誉王府",1,1,0,11001126,0,1,4,6,8407,"灰鹞摆起谱来，梅长苏无奈命飞流出手，欲强行闯入。",652,1,398,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：34~35\n普通的敌人。"},
key10001127={10001127,10012,8,"落花流水",3,1,0,11001128,0,1,4,6,8408,"灰鹞哪里是飞流的对手，几招下来被打得落花流水，二人强行入府。",658,1,1726,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：34~35\nBOSS，擅于外功攻击最前排目标。"},
key10001131={10001131,10013,1,"款款而来",1,1,0,10001127,0,1,4,6,8101,"灰鹞落败，二人正要入府，却见一眼角娇媚的女子款款而来。",656,1,413,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key10001132={10001132,10013,2,"秦般弱",1,1,0,10001131,0,1,4,6,8102,"来人原是誉王谋士秦般弱，梅长苏微眯双眼，知道不能小瞧这个女子。",655,1,413,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key10001133={10001133,10013,3,"探知实力",1,1,0,10001132,0,1,4,6,8103,"秦般弱并未无礼，言语试探梅长苏，欲探出其真实实力。",652,1,413,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：35~36\n普通的敌人。"},
key11001134={11001134,10013,4,"不耐纠缠",2,1,0,10001133,0,1,4,6,8104,"梅长苏担忧霓凰，急见誉王，不耐与其言辞纠缠，秦般弱变脸。",1580,1,413,"1014001,10;1012001,5;1,20","1,100;2,1160","推荐英雄等级：35~36\n精英，擅于对目标施加持续毒伤害。"},
key10001135={10001135,10013,5,"大打出手",1,1,0,11001134,0,1,4,6,8105,"飞流早已不耐烦，当下剑已出鞘，和秦般弱的护卫打在一起。",653,1,398,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key11001136={11001136,10013,6,"残兵败将",1,1,0,10001135,0,1,4,6,8106,"飞流出手并无轻重之分，将一干人等迅速打败，残兵败将丢了一地。",654,1,398,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key11001138={11001138,10013,7,"你们无礼",1,1,0,11001136,0,1,4,6,8107,"誉王闻声终于姗姗来迟，见到梅长苏，急忙黑脸斥责手下无礼。",657,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：36~37\n普通的敌人。"},
key10001137={10001137,10013,8,"誉王入宫",3,1,0,11001138,0,1,4,6,8108,"听闻太子算计霓凰，誉王暗道天助我也，答应梅长苏入宫相帮。",658,1,413,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：36~37\nBOSS，擅于对目标施加持续毒伤害。"},
key10001141={10001141,10014,1,"有人欢喜",1,1,0,10001137,0,1,4,6,8201,"誉王得知消息后，风风火火入宫寻到皇后，喜滋滋的简述了霓凰遇险。",653,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key10001142={10001142,10014,2,"机不可失",1,1,0,10001141,0,1,4,6,8202,"皇后并非愚钝之人，瞬间明白了个中奥妙，暗道机不可失失不再来。",654,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key10001143={10001143,10014,3,"失不再来",1,1,0,10001142,0,1,4,6,8203,"宫中秘辛，一旦戳破那层窗户纸，越贵妃和太子此次必会倒下一个。",657,1,435,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：37~38\n普通的敌人。"},
key11001144={11001144,10014,4,"直闯东宫",2,1,0,10001143,0,1,4,6,8204,"皇后誉王心喜之下直入东宫，心道梅长苏果然是麒麟才子，福将啊。",1580,1,774,"1014001,10;1012001,5;1,20","1,100;2,1180","推荐英雄等级：37~38\n精英，擅于外功攻击单体目标。"},
key10001145={10001145,10014,5,"饮下情丝绕",1,1,0,11001144,0,1,4,6,8205,"霓凰已被越贵妃哄骗饮下情丝绕，身软无力眼不视物，当即怒火攻心。",656,1,798,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key11001146={11001146,10014,6,"司马雷",1,1,0,10001145,0,1,4,6,8206,"越贵妃召来心腹司马雷，欲乘机对霓凰行下不轨之事。",655,1,1481,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key11001148={11001148,10014,7,"武将之威",1,1,0,11001146,0,1,4,6,8207,"穆霓凰久经沙场，威势仍在，急怒之下推开司马雷欲离东宫。",652,1,798,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：38~39\n普通的敌人。"},
key10001147={10001147,10014,8,"靖王赶到",3,1,0,11001148,0,1,4,6,8208,"靖王得消息火速赶往东宫，却见霓凰倒地，越贵妃怂恿司马雷欲行不轨。",658,1,1109,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：38~39\nBOSS，擅于内功攻击全体目标。"},
key10001151={10001151,10015,1,"气场骇人",1,1,0,10001147,0,1,4,6,8401,"军队历练下拥有的不仅是功夫，更有骇人气场，司马雷趔趄。",652,1,774,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001152={10001152,10015,2,"救起霓凰",1,1,0,10001151,0,1,4,6,8402,"靖王扶起霓凰，不令人靠近，越贵妃对这半路杀出的程咬金又怕又恨。",654,1,66,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001153={10001153,10015,3,"起杀心",1,1,0,10001152,0,1,4,6,8403,"宫内之事若是泄漏，太子就毁了。死人才不会乱说话，越贵妃起杀心。",653,1,435,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001154={11001154,10015,4,"弓箭手",2,1,0,10001153,0,1,4,6,8404,"越贵妃情急之下命弓箭手围靖王，想要杀人灭口死无对证。",1580,1,1488,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\nBOSS，擅于内功攻击全体目标"},
key10001155={10001155,10015,5,"挟持太子",1,1,0,11001154,0,1,4,6,8405,"对这对残害忠良将士的母子，靖王心生忿恨，飞身挟持太子。",655,1,421,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001156={11001156,10015,6,"誉王现身",1,1,0,10001155,0,1,4,6,8406,"誉王几人冲入东宫，眼见便是密密麻麻的弓箭手直指靖王与太子。",656,1,1114,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key11001158={11001158,10015,7,"杀无赦",1,1,0,11001156,0,1,4,6,8407,"越贵妃心急之下歪曲事实，称靖王狼子野心挟持太子，应当杀无赦。",657,1,66,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\n普通的敌人。"},
key10001157={10001157,10015,8,"陛下定夺",3,1,0,11001158,0,1,4,6,8408,"皇后怎会放过如此机会掰倒太子，当机立断表忠心，一切等皇上定夺。",658,1,421,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：39~40\nBOSS，擅于内功攻击全体目标。"},
key10001161={10001161,10016,1,"帝王心",1,1,0,10001157,0,1,4,6,8301,"自此，霓凰和靖王算是安全了，然而真相只靠一张嘴，谁又懂帝王心。",656,1,798,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key10001162={10001162,10016,2,"一个交代",1,1,0,10001161,0,1,4,6,8302,"霓凰长跪不起，定要皇帝给自己，给穆王府一个交代。",654,1,798,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key10001163={10001163,10016,3,"抵死不认",1,1,0,10001162,0,1,4,6,8303,"长伴枕边的越贵妃，如何不懂皇帝的软肋，定要歪曲事实死不承认。",655,1,435,"1014001,10;1012001,5;1,20","1,100;2,1200","推荐英雄等级：40~41\n普通的敌人。"},
key11001164={11001164,10016,4,"一声叹息",2,1,0,10001163,0,1,4,6,8304,"靖王本就不受宠，牵扯到太子之事，靖王的话语并无多少分量。",1580,1,1488,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：40~41\nBOSS，擅于内功攻击全体目标。"},
key10001165={10001165,10016,5,"生擒司马雷",1,1,0,11001164,0,1,4,6,8305,"梅长苏先知先行，令慕青守住越贵妃后院，果真擒住了偷溜的司马雷。",652,1,408,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key11001166={11001166,10016,6,"证据凿凿",1,1,0,10001165,0,1,4,6,8306,"蒙挚将司马雷被擒被揍的消息禀告皇帝，证据凿凿，越贵妃瘫软倒地。",653,1,69,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key11001168={11001168,10016,7,"保太子",1,1,0,11001166,0,1,4,6,8307,"梁帝震怒，欲重罚太子，越贵妃哭天喊地独自揽下罪责，欲保太子。",657,1,435,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：41~42\n普通的敌人。"},
key10001167={10001167,10016,8,"落下帷幕",3,1,0,11001168,0,1,4,6,8308,"贵妃降位，太子禁闭，靖王无褒无罚，霓凰不甘也无奈，此事于此落幕。",658,1,798,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：41~42\n精英，对随机目标施加持续毒伤害。"},
key10001171={10001171,10017,1,"功不可没",1,1,0,10001167,0,1,4,6,8201,"霓凰深知此次能够全身而退，梅长苏功不可没。",655,37,1488,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key10001172={10001172,10017,2,"情深难掩",1,1,0,10001171,0,1,4,6,8202,"霓凰约见梅长苏，郑重道谢，从对方眼中读出深情义，霓凰起疑。",654,37,794,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key10001173={10001173,10017,3,"小殊是你吗",1,1,0,10001172,0,1,4,6,8203,"两人行至原林家府邸，霓凰情绪激动，心中笃定梅长苏定是小殊。",652,37,73,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：42~43\n普通的敌人。"},
key11001174={11001174,10017,4,"终相认",2,1,0,10001173,0,1,4,6,8204,"霓凰泪盈于睫，梅长苏于心不忍终不再隐瞒，承认自己即林殊。",1580,37,1490,"1014001,10;1012001,5;1,20","1,100;2,1220","推荐英雄等级：42~43\n精英，擅于内功攻击单体目标。"},
key10001175={10001175,10017,5,"面目全非",1,1,0,11001174,0,1,4,6,8205,"得知小殊真的回来了，即便面目全非，霓凰心疼之余很是欣喜。",657,37,794,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key11001176={11001176,10017,6,"缄口不言",1,1,0,10001175,0,1,4,6,8206,"霓凰询问梅岭一役，梅长苏缄口不言，不愿霓凰承担过多。",656,37,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key11001178={11001178,10017,7,"前路漫漫",1,1,0,11001176,0,1,4,6,8207,"霓凰不愿勉强林殊，得知其未死，已心满意足。",653,37,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：43~44\n普通的敌人。"},
key10001177={10001177,10017,8,"心在靖王",3,1,0,11001178,0,1,4,6,8208,"梅长苏告知霓凰自己并非真的投在誉王门下，提及欲助靖王夺嫡。",658,37,407,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：43~44\nBOSS，擅于内功攻击全体目标。"},
key10001181={10001181,10018,1,"夺嫡的天平",1,1,0,10001177,0,1,4,6,8101,"越贵妃倒台，太子损失了左膀右臂，二龙对峙的天平开始倾斜。",652,38,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001182={10001182,10018,2,"妙音坊",1,1,0,10001181,0,1,4,6,8102,"各方势力暗潮汹涌，梅长苏思忖，妙音坊，也需要出手了。",654,38,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001183={10001183,10018,3,"宫羽姑娘",1,1,0,10001182,0,1,4,6,8103,"得知将见梅长苏，宫羽心中满是喜悦，盈盈而至。",656,38,794,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001184={11001184,10018,4,"君子无心",2,1,0,10001183,0,1,4,6,8104,"梅长苏避开宫羽的好意，仅仅谈及宫中与金陵城各方势力。",1580,38,1488,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n精英，擅于内功攻击全体目标。"},
key10001185={10001185,10018,5,"风花雪月",1,1,0,11001184,0,1,4,6,8105,"宫羽垂睫，心知盟主意不在风花雪月，脑中闪过清明，不再黯然伤神。",657,38,794,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001186={11001186,10018,6,"武力重要",1,1,0,10001185,0,1,4,6,8106,"梅长苏提出，妙音坊需要对付秦般弱的红袖招，武力亦十分重要。",655,38,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key11001188={11001188,10018,7,"七窍玲珑心",1,1,0,11001186,0,1,4,6,8107,"宫羽心思玲珑，当即唤出亲卫，多谢盟主不吝赐教。",653,38,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\n普通的敌人。"},
key10001187={10001187,10018,8,"不吝赐教",3,1,0,11001188,0,1,4,6,8108,"梅长苏并未多言，遣人与宫羽亲卫试炼，自己从旁指导。",658,38,797,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：44~45\nBOSS，擅于单体攻击后排单体目标。"},
key10001191={10001191,10019,1,"南楚来客",1,1,0,10001187,0,1,4,6,8401,"表面的金陵十分平静，此时迎来了另一重事，南楚来贵客。",652,39,796,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key10001192={10001192,10019,2,"宇文兄妹",1,1,0,10001191,0,1,4,6,8402,"南楚皇子宇文暄携妹妹宇文念入金陵，听闻此消息，梅长苏心念一动。",654,39,794,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key10001193={10001193,10019,3,"登门拜访",1,1,0,10001192,0,1,4,6,8403,"少年郎正是坐不住的年岁，有此盛事，言豫津忙拉萧景睿前往拜访。",653,39,796,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：45~46\n普通的敌人。"},
key11001194={11001194,10019,4,"比武切磋",2,1,0,10001193,0,1,4,6,8404,"鲜衣怒马言豫津，个性最是张扬，听闻宇文暄剑法奇妙，提出切磋。",1580,39,1725,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：45~46\n精英，擅于内功攻击竖列目标。"},
key10001195={10001195,10019,5,"宇文念",1,1,0,11001194,0,1,4,6,8405,"哪曾想，宇文暄尚未出手，其妹宇文念点名要与萧景睿切磋一二。",656,39,795,"1014001,10;1012001,5;1,20","1,100;2,1240","推荐英雄等级：46~47\n普通的敌人。"},
key11001196={11001196,10019,6,"点到即止",1,1,0,10001195,0,1,4,6,8406,"景睿性子沉稳，耐不住言豫津热烈怂恿，答应点到即止。",657,39,794,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：46~47\n普通的敌人。"},
key11001198={11001198,10019,7,"胜负难辨",1,1,0,11001196,0,1,4,6,8407,"二人宝剑出鞘影随身至，片刻便斗在一起，胜负难辨。",655,39,795,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：46~47\n普通的敌人。"},
key10001197={10001197,10019,8,"景睿小胜",3,1,0,11001198,0,1,4,6,8408,"宇文念行动渐慢，出招渐渐吃力，梅长苏喝止二人以免受伤。",658,39,1487,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：46~47\nBOSS，擅于内功攻击竖列目标。"},
key10001201={10001201,10020,1,"见好就收",1,1,0,10001197,0,1,4,6,8301,"宇文念萧景睿双双收手，景睿拱手客气，夸赞宇文姑娘剑法。",656,40,423,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001202={10001202,10020,2,"不怕输",1,1,0,10001201,0,1,4,6,8302,"宇文念有样学样，客气几句，小小年纪倒是十分大气。",654,40,67,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001203={10001203,10020,3,"心疼妹妹",1,1,0,10001202,0,1,4,6,8303,"妹妹落败，哥哥宇文暄看在眼中，心中却生出不满。",655,40,434,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：47~48\n普通的敌人。"},
key10001204={10001204,10020,4,"宇文暄",2,1,0,10001203,0,1,4,6,8304,"宇文暄提剑相邀，希望与梅长苏比试一番，倒惹急了景睿豫津。",1580,40,1489,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：47~48\n精英，擅于内功攻击全体目标。"},
key11001205={11001205,10020,5,"不会武",1,1,0,10001204,0,1,4,6,8305,"言豫津快人快语，提出梅长苏不会武功，宇文暄心中不信却并不相逼。",652,40,902,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：48~49\n普通的敌人。"},
key11001206={11001206,10020,6,"景睿代替",1,1,0,11001205,0,1,4,6,8306,"梅长苏坦然一笑，以身体不佳为由，提出口述技法，由景睿替自己打。",657,40,423,"1014001,10;1012001,5;1,20","1,100;2,1260","推荐英雄等级：48~49\n普通的敌人。"},
key11001207={11001207,10020,7,"江左盟主",1,1,0,11001206,0,1,4,6,8307,"宇文暄痛快应下，与景睿双双提剑，梅长苏并未藏拙，用心指点。",653,40,902,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：48~49\n普通的敌人。"},
key10001208={10001208,10020,8,"精妙之人",3,1,0,11001207,0,1,4,6,8308,"江左梅郎并非虚名，梅长苏言简意赅句句精妙，双发打得十分尽兴。",658,40,434,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：48~49\nBOSS，擅于内功攻击单体目标。"},
key10001211={10001211,10021,1,"心服口服",1,1,0,10001208,0,1,4,6,8101,"宇文暄终是败下阵来，知道自己技不如人心服口服，多谢对方赐教。",653,40,434,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001212={10001212,10021,2,"风起潮涌",1,1,0,10001211,0,1,4,6,8102,"青年男女尽兴而归，另一面金陵城却发生了一件耸人惊闻的案子。",652,40,797,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001213={10001213,10021,3,"大年夜",1,1,0,10001212,0,1,4,6,8103,"大年三十，梁帝亲赐重臣勋贵年夜食盒以示嘉奖，却喜事变命案。",655,40,797,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001214={10001214,10021,4,"凶杀案",2,1,0,10001213,0,1,4,6,8104,"前往各府赐菜的内侍，在静谧的年夜街道，俱被刺杀殒命。",1580,40,420,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n精英，擅于内功攻击全体目标。"},
key11001215={11001215,10021,5,"蒙挚被责",1,1,0,10001214,0,1,4,6,8105,"此事等同于直接打在梁帝脸上，帝王大怒，首当其责便是蒙挚。",654,40,69,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key11001216={11001216,10021,6,"诡异的案子",1,1,0,11001215,0,1,4,6,8106,"蒙挚掌管金陵巡防，金陵保卫出事，还是如此诡异凶杀案，十分头大。",657,40,69,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key11001217={11001217,10021,7,"十日之期",1,1,0,11001216,0,1,4,6,8107,"梁帝定下十日破案，蒙挚心焦寻访梅长苏，拜托其帮忙找寻凶手。",656,40,902,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\n普通的敌人。"},
key10001218={10001218,10021,8,"并不简单",3,1,0,11001217,0,1,4,6,8108,"梅长苏看破此事定然不是刺杀如此简单，派出手下细细查访。",658,40,481,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：49~50\nBOSS，擅于内功攻击竖列目标。"},
key10001221={10001221,10022,1,"世间因果",1,1,0,10001218,0,1,4,6,8401,"这世间的事情，无论抹除得多干净，都不能代表事情没有发生过。",655,40,774,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001222={10001222,10022,2,"蛛丝马迹",1,1,0,10001221,0,1,4,6,8402,"发生的事情，就一定有蛛丝马迹。事件的果，必有出事的因。",657,40,774,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001223={10001223,10022,3,"了无痕迹",1,1,0,10001222,0,1,4,6,8403,"反倒因为了无痕迹，便露出马脚。能做到如此之人，世上有几个？",656,40,774,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：50~51\n普通的敌人。"},
key10001224={10001224,10022,4,"心知肚明",2,1,0,10001223,0,1,4,6,8404,"梅长苏心中其实已有答案，然而案子需要证据，巡防营依旧忙碌找寻。",1580,40,756,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：50~51\n精英，擅于内功攻击竖列目标。"},
key10001225={10001225,10022,5,"引蛇出洞",1,1,0,10001224,0,1,4,6,8405,"梅长苏让蒙挚放出假风声，巡防营已经找到重要证据，引蛇出洞。",653,40,69,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key11001226={11001226,10022,6,"神秘高手",1,1,0,10001225,0,1,4,6,8406,"果不其然出现神秘的高手阻碍查证，剑法诡秘，侍卫们均不是对手。",652,40,438,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key11001227={11001227,10022,7,"自乱阵脚",1,1,0,11001226,0,1,4,6,8407,"梅长苏心底冷笑，小小计谋，对方已经如此按耐不住自乱阵脚。",654,40,902,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：51~52\n普通的敌人。"},
key10001228={10001228,10022,8,"正合心意",3,1,0,11001227,0,1,4,6,8408,"看来对方，比自己还要心急啊，神秘高手的出现正合心意。",658,40,438,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：51~52\nBOSS，擅于内功攻击全体目标。"},
key10001231={10001231,10023,1,"请君入瓮",1,1,0,10001228,0,1,4,6,8201,"梅长苏蒙挚早已布下埋伏，一切意在请君入瓮，果然擒住了神秘高手。",653,40,69,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：52~53\n普通的敌人。"},
key10001232={10001232,10023,2,"真面目",1,1,0,10001231,0,1,4,6,8202,"黑衣人目露惊恐，蒙挚上前道：待我除你面纱，看看你的真面目！，",652,40,69,"1014001,10;1012001,5;1,20","1,100;2,1280","推荐英雄等级：52~53\n普通的敌人。"},
key10001233={10001233,10023,3,"卓青遥",1,1,0,10001232,0,1,4,6,8203,"面纱掀开，现于人前的竟是天泉山庄卓青遥，景睿的卓家哥哥兼妹夫。",657,40,438,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：52~53\n普通的敌人。"},
key10001234={10001234,10023,4,"天泉山庄",2,1,0,10001233,0,1,4,6,8204,"蒙挚十分震惊，江湖人不管朝堂事，不懂为何卓家参与这宫围凶案。",1580,40,438,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：52~53\n精英，擅于内功攻击全体目标。"},
key10001235={10001235,10023,5,"可怜人",1,1,0,10001234,0,1,4,6,8205,"梅长苏面色如常，似是早已知情，望着桌青遥的眼神不知觉带上怜悯。",654,40,902,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key11001236={11001236,10023,6,"江左暗卫",1,1,0,10001235,0,1,4,6,8206,"插手调查的是江湖人士，梅长苏看出蒙挚的为难，吟笛唤出江左暗卫。",656,40,1773,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key11001237={11001237,10023,7,"飞影至",1,1,0,11001236,0,1,4,6,8207,"江左盟，即便卓泉山庄也畏惧十分，瞬间，一道飞影掳走了卓青遥。",655,40,438,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：53~54\n普通的敌人。"},
key10001238={10001238,10023,8,"卓鼎风",3,1,0,11001237,0,1,4,6,8208,"蒙挚欲追，梅长苏将其拉住，如此身手，只有天泉山庄庄主卓鼎风了。",658,40,437,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：53~54\nBOSS，擅于内功攻击竖列目标。"},
key10001241={10001241,10024,1,"并非所盼",1,1,0,10001238,0,1,4,6,8101,"梅长苏等的，就是卓鼎风有没有参与，等来了该来的，心却异常堵闷。",654,40,902,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001242={10001242,10024,2,"景睿的生辰",1,1,0,10001241,0,1,4,6,8102,"无论发生了什么事情，金陵城该行的轨迹并无变化，比如景睿的生辰。",656,40,423,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001243={10001243,10024,3,"贵公子",1,1,0,10001242,0,1,4,6,8103,"景睿同是谢家与卓家公子，更是冠天家姓氏的贵人，生辰规模盛大。",653,40,423,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001244={10001244,10024,4,"门庭若市",2,1,0,10001243,0,1,4,6,8104,"除却身份不说，景睿交友一向亲善，人缘甚好，一时间谢府门庭若市。",1580,40,1726,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n精英，擅于内功攻击单体目标。"},
key10001245={10001245,10024,5,"不速之客",1,1,0,10001244,0,1,4,6,8105,"景睿今日一身儒贵，举手投足风度翩翩，此时却迎来一位不速之客。",657,40,423,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key11001246={11001246,10024,6,"掌镜史夏冬",1,1,0,10001245,0,1,4,6,8106,"悬镜司掌镜史夏冬携剑登门，众人避让，一时之间猜测纷纷。",655,40,419,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key11001247={11001247,10024,7,"刺杀案",1,1,0,11001246,0,1,4,6,8107,"两人私交甚好，景睿不明夏冬来意，夏冬抬手安抚，行至梅长苏身前。",653,40,902,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\n普通的敌人。"},
key10001248={10001248,10024,8,"江湖中人",3,1,0,11001247,0,1,4,6,8108,"就查案来说，夏冬与梅长苏关注的都是一个点，必是江湖高手下的手。",658,40,419,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：54~55\nBOSS，擅于内功攻击竖列目标。"},
key10001251={10001251,10025,1,"静候真相",1,1,0,10001248,0,1,4,6,8201,"夏冬怀疑江左盟与刺杀案逃不开关系，梅长苏却一句话便稳住了夏冬。",656,40,419,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001252={10001252,10025,2,"热闹纷呈",1,1,0,10001251,0,1,4,6,8202,"夏冬收起兵器，只当来参加生日宴，众人逐渐放开，宴会热闹起来。",655,40,419,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001253={10001253,10025,3,"南楚公子",1,1,0,10001252,0,1,4,6,8203,"说话间迎来了几位特殊的客人，南楚宇文暄兄妹和一位公子登门拜访。",652,40,67,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：55~56\n普通的敌人。"},
key10001254={10001254,10025,4,"岳秀泽",2,1,0,10001253,0,1,4,6,8204,"来人原是琅琊高手榜排行第五的剑客岳秀泽，意在以武会友。",1580,40,67,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：55~56\n精英，擅于内功攻击竖列目标。"},
key10001255={10001255,10025,5,"卓家剑法",1,1,0,10001254,0,1,4,6,8205,"卓家剑法闻名天下，岳秀择诚意相邀，众目睽睽卓鼎风无法回绝。",653,40,437,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：56~57\n普通的敌人。"},
key11001256={11001256,10025,6,"高手过招",1,1,0,10001255,0,1,4,6,8206,"卓鼎风应下比试，二人转眼之间便斗在一起，剑影翩翩，眼花缭乱。",657,40,1115,"1014001,10;1012001,5;1,20","1,100;2,1300","推荐英雄等级：56~57\n普通的敌人。"},
key11001257={11001257,10025,7,"怀疑的种子",1,1,0,11001256,0,1,4,6,8207,"望着二人缠斗的身影，蒙挚夏冬微微眯眼，心中怀疑大盛。",652,40,419,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：56~57\n普通的敌人。"},
key10001258={10001258,10025,8,"心中之苦",3,1,0,11001257,0,1,4,6,8208,"回望蒙挚和夏冬，卓鼎风心中苦笑，若出全力，便要暴露刺客身份。",658,40,437,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：56~57\nBOSS，擅于内功攻击全体目标。"},
key10001261={10001261,10026,1,"毅然绝然",1,1,0,10001258,0,1,4,6,8301,"剑至身前，卓鼎风心中已有决意，动作带出一股决然之气。",656,40,437,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001262={10001262,10026,2,"唯快不破",1,1,0,10001261,0,1,4,6,8302,"天下武功唯快不破，眨眼间众人便看到一片血雾飞起，两人停下动作。",655,40,1115,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001263={10001263,10026,3,"壮士断腕",1,1,0,10001262,0,1,4,6,8303,"待人看清，一阵惊呼。卓鼎风一腕断裂血如泉涌，岳秀泽面露震惊。",657,40,437,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：57~58\n普通的敌人。"},
key10001264={10001264,10026,4,"已有答案",2,1,0,10001263,0,1,4,6,8304,"卓鼎风稳住伤势，抬手认败。夏冬与蒙挚互望一刹，心中已有答案。",1580,40,1725,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：57~58\n精英，擅于内功攻击最前排目标。"},
key10001265={10001265,10026,5,"线索断",1,1,0,10001264,0,1,4,6,8305,"卓鼎风的壮士断腕，将刺杀案最后的线索一并断掉。",652,40,1115,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key11001266={11001266,10026,6,"妙音坊宫羽",1,1,0,10001265,0,1,4,6,8306,"比武结束，宴会继续，妙音坊宫羽姑娘盈盈而至，抚琴之音犹如天籁。",654,40,401,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key11001267={11001267,10026,7,"杀手相思",1,1,0,11001266,0,1,4,6,8307,"琴声戛然而止，宫羽突然道出自己凄苦身世，生父竟是消失的杀手相思。",653,40,1113,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：58~59\n普通的敌人。"},
key10001268={10001268,10026,8,"来不及了",3,1,0,11001267,0,1,4,6,8308,"宫羽道出当年父亲受雇于谢玉，杀害谢卓两家新生婴儿。",658,40,1113,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：58~59\nBOSS，擅于外功攻击单体目标。"},
key10001271={10001271,10027,1,"当年真相",1,1,0,10001268,0,1,4,6,8401,"然而相思只来得及杀害了其中一个孩子，便是卓家的孩儿。",652,40,437,"1014001,10;1012001,5;1,20","1,100;2,1320","推荐英雄等级：59~60\n普通的敌人。"},
key10001272={10001272,10027,2,"恼羞成怒",1,1,0,10001271,0,1,4,6,8402,"宫羽黯然垂泪，父亲因为此事，终日被谢玉追杀，终是死于非命。",656,40,1113,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001273={10001273,10027,3,"谢玉的阴谋",1,1,0,10001272,0,1,4,6,8403,"谢玉眼看不妙，欲阻止宫羽，被大惊的卓鼎风制止，再欲出手已晚。",653,40,424,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001274={10001274,10027,4,"面对质问",2,1,0,10001273,0,1,4,6,8404,"宫羽冷笑，指出若是不信，大可去问莅阳长公主的老嬷嬷。",1580,40,1726,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n精英，擅于外功攻击单体目标。"},
key10001275={10001275,10027,5,"杀人灭口",1,1,0,10001274,0,1,4,6,8405,"卓鼎风眼看恼羞成怒的谢玉，面色凄然的莅阳长公主，终于相信。",657,40,437,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key11001276={11001276,10027,6,"卓家的孩儿",1,1,0,10001275,0,1,4,6,8406,"原来谢玉不仅指示杀手杀害了自家孩儿，还利用天泉山庄这么多年。",654,40,437,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key11001277={11001277,10027,7,"质问谢玉",1,1,0,11001276,0,1,4,6,8407,"卓鼎风怒急攻心，莅阳长公主亦是泪流满面忿恨的质问谢玉。",655,40,400,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\n普通的敌人。"},
key10001278={10001278,10027,8,"悔不当初",3,1,0,11001277,0,1,4,6,8408,"宾客哗然，卓鼎风得知真相，亦锥心后悔这些年为谢玉做的歹事。",658,40,424,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：59~60\nBOSS，擅于内功攻击单体目标。"},
key10001281={10001281,10028,1,"召来私兵",1,1,0,10001278,0,1,4,6,8201,"一场生日宴演变成金陵秘闻，谢玉大骇，召来巡防营欲杀人灭口。",652,40,424,"1014001,10;1012001,5;1,20","1,100;2,1340","推荐英雄等级：60\n普通的敌人。"},
key11001282={11001282,10028,2,"身败名裂",1,1,0,10001281,0,1,4,6,8202,"在场皆是权贵，今日之事一旦泄露，谢玉心知自己必将身败名裂。",653,40,424,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001283={10001283,10028,3,"冰山一角",1,1,0,11001282,0,1,4,6,8203,"宫羽说出的，只是冰山一角，之后引出的事情，谢玉不敢想象。",654,40,401,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001284={10001284,10028,4,"都得死！",2,1,0,10001283,0,1,4,6,8204,"听了不该听的人，都得死！谢玉目赤，已然丧失理智。",1580,40,1725,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n精英，擅于内功攻击最前排目标。"},
key10001285={10001285,10028,5,"关门杀人",1,1,0,10001284,0,1,4,6,8205,"谢玉大手一挥，谢家大门嘎然关闭，宾客顿时乱作一团。",655,40,424,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n普通的敌人。"},
key11001286={11001286,10028,6,"血洗谢府",1,1,0,10001285,0,1,4,6,8206,"谢玉召来亲兵士卫，意图血洗谢府，来个死无对证。",656,40,424,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n普通的敌人。"},
key11001287={11001287,10028,7,"一夜成人",1,1,0,11001286,0,1,4,6,8207,"眼看着疯狂的父亲，景睿面如金纸，扶着莅阳长公主，似是一夜成人。",657,40,423,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\n普通的敌人。"},
key10001288={10001288,10028,8,"走下去",3,1,0,11001287,0,1,4,6,8208,"望着不再温润的景睿，梅长苏眼中闪过不忍，然而这条路，必须走下去。",658,40,424,"1014001,10;1012001,5;1,20","1,100;2,1380","推荐英雄等级：60\nBOSS，擅于内功攻击单体目标。"},
key10001291={10001291,10029,1,"结束是开始",1,1,0,10001288,0,1,4,6,8101,"谢玉伏法，写下罪状书存于莅阳公主，这书能保谢玉一命却也是祸根。",657,60,400,"1014001,10;1012001,5;1,20","1,100;2,1400","推荐英雄等级：60~61\n普通的敌人。"},
key10001292={10001292,10029,2,"辞别",1,1,0,10001291,0,1,4,6,8102,"景睿为谢玉送行，曾几何时父亲二字是那么顺口，如今却再也叫不出来。",652,60,423,"1014001,10;1012001,5;1,20","1,100;2,1400","推荐英雄等级：60~61\n普通的敌人。"},
key10001293={10001293,10029,3,"送君歌",1,1,0,10001292,0,1,4,6,8103,"莅阳公主看着这一生所恨却又夫妻十余年的谢玉，无语凝噎。再见了。",653,60,400,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001294={10001294,10029,4,"得失",2,1,0,10001293,0,1,4,6,8104,"寻与靖王聊予谢玉倒台之得失，靖王明翻案之志。",1580,60,63,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n精英，外防极高，推荐内功英雄。"},
key10001295={10001295,10029,5,"金钟二十七",1,1,0,10001294,0,1,4,6,8105,"金钟二十七大丧之音，太奶奶去世了。",654,60,66,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key11001296={11001296,10029,6,"昔日情",1,1,0,10001295,0,1,4,6,8106,"想起昔日太奶奶疼爱，梅长苏心下难过。",655,60,902,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key11001297={11001297,10029,7,"心病",1,1,0,11001296,0,1,4,6,8107,"悲从中来，金陵之秋怎如此萧索难熬。",656,60,902,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001298={10001298,10029,8,"登山拜祭",3,1,0,11001297,0,1,4,6,8108,"梅长苏登远山拜祭太奶奶，知秋登高，茱萸无人。",658,60,774,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\nBOSS，外防极高，推荐内功英雄。"},
key10001301={10001301,10030,1,"毒蛇",1,1,0,10001298,0,1,4,6,8201,"誉王来拜访，梅长苏仍于悲伤之中强掩厌恶之情为之献策。",654,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001302={10001302,10030,2,"近夕之兆",1,1,0,10001301,0,1,4,6,8202,"分析誉王得利，太子之势力几乎被连根拔除，誉王喜不自禁。",655,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001303={10001303,10030,3,"红袖招蕊",1,1,0,10001302,0,1,4,6,8203,"誉王向梅长苏诉言般弱红袖招失利，不再好用，梅长苏一笑而过。",652,60,902,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001304={10001304,10030,4,"眼线被拔",2,1,0,10001303,0,1,4,6,8204,"秦般弱找来，质疑是梅长苏动了她的眼线。",1580,60,1490,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n精英，内防极高，推荐外功英雄。"},
key10001305={10001305,10030,5,"针锋相对",1,1,0,10001304,0,1,4,6,8205,"针锋相对，秦般弱认定妙音坊与梅长苏有关，直言会将之拔除。",653,60,413,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key11001306={11001306,10030,6,"妙音坊乐",1,1,0,10001305,0,1,4,6,8206,"妙音坊的头牌宫羽接报准备，寻反胜之击。",656,60,401,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key11001307={11001307,10030,7,"杨柳心舞",1,1,0,11001306,0,1,4,6,8207,"宫羽找来杨柳心做帮手，女人的战争另有她法。",657,60,401,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\n普通的敌人。"},
key10001308={10001308,10030,8,"滑族之心",3,1,0,11001307,0,1,4,6,8208,"秦般弱征程之前回顾生平所志，滑族之心，家国之仇，终难弃。",658,60,413,"1014001,10;1012001,5;1,20","1,100;2,1420","推荐英雄等级：60~61\nBOSS，内防极高，推荐外功英雄。"},
key10001311={10001311,10031,1,"滑族暗线",1,1,0,10001308,0,1,4,6,8301,"认定了梅长苏就是幕后之手，秦般弱寻师父璇姬公主所留暗线。",653,60,426,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001312={10001312,10031,2,"隽娘",1,1,0,10001311,0,1,4,6,8302,"终有合适人选，即是隐居在外的师姐隽娘。",652,60,67,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001313={10001313,10031,3,"鱼死网破",1,1,0,10001312,0,1,4,6,8303,"昔日滑族为大梁灭国，全族女子皆委身大梁皇族，含苦吞耻。",656,60,426,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001314={10001314,10031,4,"刻骨深仇",2,1,0,10001313,0,1,4,6,8304,"耻辱吞心为图报仇倾刻，滑族仅存千余女子却宁为玉碎不为瓦全。",1580,60,1488,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n精英，内防极高，推荐外功英雄。"},
key10001315={10001315,10031,5,"景睿离踪",1,1,0,10001314,0,1,4,6,8305,"雪庐事变，言豫津来找梅长苏，因景睿不知所踪。",655,60,430,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key11001316={11001316,10031,6,"南楚事变",1,1,0,10001315,0,1,4,6,8306,"终于在城外寻到萧景睿，原来是南楚事变，景睿生父宇文霖重病。",654,60,433,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key11001317={11001317,10031,7,"宇文念之心",1,1,0,11001316,0,1,4,6,8307,"一边关心兄长景睿，一边牵挂父亲宇文霖，宇文念有苦难言。",657,60,423,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001318={10001318,10031,8,"景睿南行",3,1,0,11001317,0,1,4,6,8308,"景睿心意已决，虽说要行向南楚，却并非不归，让豫津安心。",658,60,1488,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\nBOSS，内防极高，推荐外功英雄。"},
key10001321={10001321,10032,1,"东宫事变",1,1,0,10001318,0,1,4,6,8401,"宫中要事发生，梅长苏会面蒙挚靖王，雪庐详谈。",657,60,902,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001322={10001322,10032,2,"太子被禁",1,1,0,10001321,0,1,4,6,8402,"太子被幽禁东宫，朝堂百官相扰，蒙挚奉口谕封宫，苦苦相撑。",656,60,69,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001323={10001323,10032,3,"废黜太子诏",1,1,0,10001322,0,1,4,6,8403,"蒙挚请明旨为高湛打断，梅长苏指明他所请的是废黜太子的诏书。",655,60,399,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n普通的敌人。"},
key10001324={10001324,10032,4,"难耐的誉王",2,1,0,10001323,0,1,4,6,8404,"誉王同样得知消息来找梅长苏相议，如何让太子永不翻身。",1580,60,1488,"1014001,10;1012001,5;1,20","1,100;2,1440","推荐英雄等级：61~62\n精英，内防极高，推荐外功英雄。"},
key10001325={10001325,10032,5,"急功近利",1,1,0,10001324,0,1,4,6,8405,"誉王急功近利，不听梅长苏相劝终究走上了梅长苏所意料的路上。",654,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：61~62\n普通的敌人。"},
key11001326={11001326,10032,6,"暗谋",1,1,0,10001325,0,1,4,6,8406,"梅长苏智计无双，相劝誉王要稳中求胜不可冒进也不可不进。",653,60,902,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：61~62\n普通的敌人。"},
key11001327={11001327,10032,7,"不可不进",1,1,0,11001326,0,1,4,6,8407,"梅长苏之相劝引得誉王逼近，却一样为梅长苏留下退路。",652,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：61~62\n普通的敌人。"},
key10001328={10001328,10032,8,"宫中事",3,1,0,11001327,0,1,4,6,8408,"誉王以亲王之资威压太子风头无两，却不知帝王之心，另有他算。",658,60,415,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：61~62\nBOSS，外防极高，推荐内功英雄。"},
key10001331={10001331,10033,1,"靖王加封",1,1,0,10001328,0,1,4,6,8101,"靖王加封五珠亲王，誉王匆匆来访，言谈之中满是惊怒与不甘。",656,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001332={10001332,10033,2,"五珠亲王",1,1,0,10001331,0,1,4,6,8102,"梅长苏恭贺誉王，这说明太子快被废了，皇上需要新的平衡局势而已。",655,60,902,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001333={10001333,10033,3,"誉王警觉",1,1,0,10001332,0,1,4,6,8103,"誉王焦虑不已，花十年斗倒了太子，难道还要花十年去斗靖王？",652,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001334={10001334,10033,4,"破计",2,1,0,10001333,0,1,4,6,8104,"靖王怎可与殿下相比？梅长苏假意迎合，心中却知此番糊弄不过去了。",1580,60,63,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n精英，外防极高，推荐内功英雄。"},
key10001335={10001335,10033,5,"反击",1,1,0,10001334,0,1,4,6,8105,"誉王心事重重地回府，秦般弱见其颓废，直言梅长苏已为靖王所得。",653,60,413,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key11001336={11001336,10033,6,"曲弈密谋",1,1,0,10001335,0,1,4,6,8106,"誉王虽难不愿相信，也不得不承认自己被麒麟才子狠狠耍了一道。",654,60,1114,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key11001337={11001337,10033,7,"重振旗鼓",1,1,0,11001336,0,1,4,6,8107,"秦般弱重获信任，坦言红袖招是为梅长苏拔除，但一切还有转机。",657,60,413,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001338={10001338,10033,8,"计上心头",3,1,0,11001337,0,1,4,6,8108,"梅长苏之所以屡屡成功，只因他在暗我在明，接下来他不会这么顺利了。",658,60,413,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\nBOSS，内防极高，推荐外功英雄。"},
key10001341={10001341,10034,1,"无功无过",1,1,0,10001338,0,1,4,6,8201,"秦般弱提议对梅长苏下手，去除靖王的智囊。对方必然手足无措。",653,62,902,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001342={10001342,10034,2,"誉王之计",1,1,0,10001341,0,1,4,6,8202,"誉王不赞同，局势已定，此刻拔除梅长苏意义不大，只能针对靖王。",654,62,66,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001343={10001343,10034,3,"臂助",1,1,0,10001342,0,1,4,6,8203,"靖王和皇上之间最大的矛盾，便是十三年前那桩旧案，可是从何下手？",657,62,1111,"1014001,10;1012001,5;1,20","1,100;2,1460","推荐英雄等级：62~63\n普通的敌人。"},
key10001344={10001344,10034,4,"夏首尊",2,1,0,10001343,0,1,4,6,8204,"秦般弱想到有一人可相助誉王，誉王忙问是谁，秦般弱提出夏江。",1580,62,774,"1014001,10;1012001,5;1,20","1,100;2,1480","推荐英雄等级：62~63\n精英，外防极高，推荐内功英雄。"},
key10001345={10001345,10034,5,"莅阳之危",1,1,0,10001344,0,1,4,6,8205,"谢玉流放之后，莅阳公主独自出行，却在路上遭杀手袭击暗杀。",656,62,400,"1014001,10;1012001,5;1,20","1,100;2,1480","推荐英雄等级：62~63\n普通的敌人。"},
key11001346={11001346,10034,6,"悬镜司之择",1,1,0,10001345,0,1,4,6,8206,"梅长苏派去保护的人马救下了莅阳公主，夏江在一侧阁楼上皱起眉头。",655,62,1482,"1014001,10;1012001,5;1,20","1,100;2,1480","推荐英雄等级：62~63\n普通的敌人。"},
key11001347={11001347,10034,7,"春秋冬",1,1,0,11001346,0,1,4,6,8207,"夏江无功而返，三位高徒已在悬镜司等候，一切井井有条。",652,62,1482,"1014001,10;1012001,5;1,20","1,100;2,1480","推荐英雄等级：62~63\n普通的敌人。"},
key10001348={10001348,10034,8,"长相思",3,1,0,11001347,0,1,4,6,8208,"看到夏冬时，夏江产生了一丝焦虑，决意一定要销毁谢玉的手书。",658,62,775,"1014001,10;1012001,5;1,20","1,100;2,1480","推荐英雄等级：62~63\nBOSS，内防极高，推荐外功英雄。"},
key10001351={10001351,10035,1,"朝堂权雾",1,1,0,10001348,0,1,4,6,8101,"夏江受邀来到誉王府，秦般弱殷切攀谈，誉王还抱有一丝怀疑。",657,62,1482,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001352={10001352,10035,2,"谗天啼诉",1,1,0,10001351,0,1,4,6,8102,"夏江言明会助誉王扳倒靖王，但自己只针对靖王，之后不会参与党争。",652,62,66,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001353={10001353,10035,3,"腥风血雨",1,1,0,10001352,0,1,4,6,8103,"虽然斩断了谢玉和夏江之间的信任，梅长苏依然对夏江十分提防。",653,62,902,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001354={10001354,10035,4,"靖王府之音",2,1,0,10001353,0,1,4,6,8104,"更深人静，密道中的铃声突然响起，梅长苏起身去与靖王会面。",1580,62,1782,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n精英，内防极高，推荐外功英雄。"},
key10001355={10001355,10035,5,"水牛",1,1,0,10001354,0,1,4,6,8105,"靖王问起飞流所呼水牛之事，梅长苏笑谈郡主所言，搪塞了过去。",654,62,66,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001356={10001356,10035,6,"悬镜司夜监",1,1,0,10001355,0,1,4,6,8106,"如今夏江在京，万事皆需小心，切勿露出破绽被其抓到。",655,62,902,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001357={10001357,10035,7,"长胜歌",1,1,0,10001356,0,1,4,6,8107,"靖王称是，二人相谈片刻后靖王离去，梅长苏却不能放下心来。",656,62,902,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001358={10001358,10035,8,"援手",3,1,0,10001357,0,1,4,6,8108,"梅长苏告诉黎刚，靖王一向不在意自己，江左盟这边还须多加照应。",658,62,1488,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\nBOSS，内防极高，推荐外功英雄。"},
key10001361={10001361,10036,1,"言府",1,1,0,10001358,0,1,4,6,8201,"言家，家出三代帝师，两位皇后，丹书铁券世家，靠的不仅是祖辈福茵。",654,62,430,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001362={10001362,10036,2,"言阙",1,1,0,10001361,0,1,4,6,8202,"言阙，当年力谏大渝北燕，智谋勇气能与蔺相如相比。",655,62,429,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001363={10001363,10036,3,"冷眼相待",1,1,0,10001362,0,1,4,6,8203,"梅长苏拜访言府，言侯不欲帮扶誉王，冷眼相待。",652,62,429,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n普通的敌人。"},
key10001364={10001364,10036,4,"真相",2,1,0,10001363,0,1,4,6,8204,"梅长苏浅言，所图所谋所为，何曾是为了誉王？言阙得知心下惊讶。",1580,62,63,"1014001,10;1012001,5;1,20","1,100;2,1500","推荐英雄等级：63~64\n精英，外防极高，推荐内功英雄。"},
key10001365={10001365,10036,5,"火眼金睛",1,1,0,10001364,0,1,4,6,8205,"靖王地位确实不足以夺嫡，言阙提出，选择靖王，不合情理，无胜利把握。",653,62,66,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：63~64\n普通的敌人。"},
key10001366={10001366,10036,6,"愿意吗",1,1,0,10001365,0,1,4,6,8206,"梅长苏并未反对言阙，甚至默许，只是提出，言侯，是否愿意相帮靖王？",656,62,902,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：63~64\n普通的敌人。"},
key10001367={10001367,10036,7,"誓死相随",1,1,0,10001366,0,1,4,6,8207,"言侯深知靖王性情，梅长苏之言燃起心中热血，表示愿意誓死相随。",657,62,429,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：63~64\n普通的敌人。"},
key10001368={10001368,10036,8,"我愿意",3,1,0,10001367,0,1,4,6,8208,"豫津虽并未完全明白二人所图，却表示愿意听从父亲，跟随靖王。",658,62,1109,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：63~64\nBOSS，外防极高，推荐内功英雄。"},
key10001371={10001371,10037,1,"重病",1,1,0,10001368,0,1,4,6,8301,"日渐入冬，寒气袭来，梅长苏身体不支，病倒了。",653,62,902,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001372={10001372,10037,2,"修养",1,1,0,10001371,0,1,4,6,8302,"晏大夫很是生气，制止梅长苏操劳，嘱咐必须休息。",652,62,431,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001373={10001373,10037,3,"夏江的手段",1,1,0,10001372,0,1,4,6,8303,"梅长苏担忧，夏江已经回京，以他的诡谲，定然有阴谋。",656,62,1482,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001374={10001374,10037,4,"万事有我们",2,1,0,10001373,0,1,4,6,8304,"黎刚甄平忧心梅长苏身体，同晏大夫一同劝梅长苏休息。",1580,62,1782,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n精英，内防极高，推荐外功英雄。"},
key10001375={10001375,10037,5,"睡去",1,1,0,10001374,0,1,4,6,8305,"梅长苏抵不过众人好意，服下药沉沉睡去。",655,62,902,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001376={10001376,10037,6,"铲除梅长苏",1,1,0,10001375,0,1,4,6,8306,"秦般弱劝誉王可联合夏江铲除梅长苏与靖王，誉王被劝服。",654,62,413,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001377={10001377,10037,7,"赤焰旧人",1,1,0,10001376,0,1,4,6,8307,"誉王与夏江会面，夏江提出有赤焰旧人卫铮的线索，可利用他攻击靖王。",657,62,1482,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\n普通的敌人。"},
key10001378={10001378,10037,8,"阴谋",3,1,0,10001377,0,1,4,6,8308,"赤焰旧人是靖王与梁帝心中的刺，夏江誉王开始布下阴谋。",658,62,63,"1014001,10;1012001,5;1,20","1,100;2,1520","推荐英雄等级：64~65\nBOSS，外防极高，推荐内功英雄。"},
key10001381={10001381,10038,1,"童路被捉",1,1,0,10001378,0,1,4,6,8401,"夏江密谋的同时，秦般弱查出了童路。",657,62,413,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001382={10001382,10038,2,"童路之叛",1,1,0,10001381,0,1,4,6,8402,"秦般弱趁夜色捉住了童路，利用隽娘，逼童路说出线索。",656,62,413,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001383={10001383,10038,3,"危机",1,1,0,10001382,0,1,4,6,8403,"童路不愿隽娘受伤害，不得已供出了妙音坊。",655,62,401,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001384={10001384,10038,4,"人去楼空",2,1,0,10001383,0,1,4,6,8404,"秦般弱立即派遣誉王的府兵查抄了妙音坊，然而却人去楼空。",1580,62,1488,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n精英，内防极高，推荐外功英雄。"},
key10001385={10001385,10038,5,"埋伏药王谷",1,1,0,10001384,0,1,4,6,8405,"与此同时，夏江派出的悬镜使夏秋，已埋伏在药王谷附近。",654,62,420,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001386={10001386,10038,6,"素玄卫铮",1,1,0,10001385,0,1,4,6,8406,"药王谷少谷主，素玄，即是当年林殊的前锋卫铮。",653,62,418,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001387={10001387,10038,7,"被捕",1,1,0,10001386,0,1,4,6,8407,"卫铮并不知道夏江的阴谋，毫无准备落入夏秋的追捕陷阱。",652,62,418,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\n普通的敌人。"},
key10001388={10001388,10038,8,"押解入京",3,1,0,10001387,0,1,4,6,8408,"卫铮被捕，夏秋将其锁入牢车，重兵押解入京。",658,62,774,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：64~65\nBOSS，外防极高，推荐内功英雄。"},
key10001391={10001391,10039,1,"深眠养病",1,1,0,10001388,0,1,4,6,8101,"金陵苏宅，梅长苏在晏大夫的逼迫下服药进入深眠，修养病体。",657,65,431,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：65~66\n普通的敌人。"},
key10001392={10001392,10039,2,"急报入京",1,1,0,10001391,0,1,4,6,8102,"梅长苏沉睡之时，药王谷遣来报信的人马已抵达苏宅。",652,65,902,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：65~66\n普通的敌人。"},
key10001393={10001393,10039,3,"商议",1,1,0,10001392,0,1,4,6,8103,"甄平黎刚忙问晏大夫能否让宗主清醒片刻，作下决断。",653,65,436,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：65~66\n普通的敌人。"},
key10001394={10001394,10039,4,"无奈之举",2,1,0,10001393,0,1,4,6,8104,"晏大夫严词拒绝，二人只好自行决定组织人手营救卫峥。",1580,65,774,"1014001,10;1012001,5;1,20","1,100;2,1540","推荐英雄等级：65~66\n精英，外攻免疫，推荐内功英雄。"},
key10001395={10001395,10039,5,"连夜雨",1,1,0,10001394,0,1,4,6,8105,"另一边宫中，皇后以静妃用毒草药谋害皇上的罪名，幽禁静妃。",654,65,402,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001396={10001396,10039,6,"宫女小新",1,1,0,10001395,0,1,4,6,8106,"皇上远在陵寝，宫女小新借口如厕偷偷溜出，报信靖王府。",655,65,425,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001397={10001397,10039,7,"六神无主",1,1,0,10001396,0,1,4,6,8107,"列战英听闻，连忙使戚猛和小新去陵寝通知靖王，自己疾驰至苏宅。",656,65,405,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001398={10001398,10039,8,"求助苏宅",3,1,0,10001397,0,1,4,6,8108,"列战英在苏宅门口被黎刚拦下，被告知苏先生病重，不能相帮。",658,65,436,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\nBOSS，外攻免疫，推荐内功英雄。"},
key10001401={10001401,10040,1,"拦截",1,1,0,10001398,0,1,4,6,8201,"戚猛和小新路上被神秘人拦截，言及苏先生要静妃忍耐几日。",653,65,425,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001402={10001402,10040,2,"靖王回京",1,1,0,10001401,0,1,4,6,8202,"几日后靖王回京，被誉王直接引进宫中，夏江提起卫峥被抓一事。",654,65,1482,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001403={10001403,10040,3,"步步相逼",1,1,0,10001402,0,1,4,6,8203,"二人一唱一和，意图激怒靖王说出不敬之言，果不其然，皇帝大怒。",657,65,66,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001404={10001404,10040,4,"城门急救",2,1,0,10001403,0,1,4,6,8204,"甄平携飞流与江左部下在城门劫囚，不想悬镜司早有准备，不得不撤。",1580,65,1725,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n精英，外攻免疫，推荐内功英雄。"},
key10001405={10001405,10040,5,"踏入陷阱",1,1,0,10001404,0,1,4,6,8205,"靖王前往拜见母妃，正因卫峥之事苦恼，小新突然上前大倒苦水。",656,65,425,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001406={10001406,10040,6,"诛心之言",1,1,0,10001405,0,1,4,6,8206,"小新说苏先生认为静妃不过委屈几日罢了，还能趁机行苦肉计。",655,65,425,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001407={10001407,10040,7,"闻言心惊",1,1,0,10001406,0,1,4,6,8207,"静妃连忙阻止，但靖王已将种种听进心去，对梅长苏产生了愤恨。",652,65,66,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\n普通的敌人。"},
key10001408={10001408,10040,8,"心有隔阂",3,1,0,10001407,0,1,4,6,8208,"靖王急躁回府，列战英将几日情形细细说之，提起苏宅求救被拒一事。",658,65,434,"1014001,10;1012001,5;1,20","1,100;2,1560","推荐英雄等级：65~66\nBOSS，外攻免疫，推荐内功英雄。"},
key10001411={10001411,10041,1,"对峙",1,1,0,10001408,0,1,4,6,8301,"靖王来到地道，按捺心中怒气，与梅长苏相谈营救卫峥之事。",657,65,902,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001412={10001412,10041,2,"割舍",1,1,0,10001411,0,1,4,6,8302,"不料梅长苏却说，谋大事者，须懂得割舍，救卫峥百害而无一利。",652,65,418,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001413={10001413,10041,3,"摩擦",1,1,0,10001412,0,1,4,6,8303,"靖王怒火中烧，不听列战英劝阻，一剑砍断了地道传音的铃铛。",653,65,66,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001414={10001414,10041,4,"分裂",2,1,0,10001413,0,1,4,6,8304,"我萧景琰今后何去何从，便不劳梅宗主费心了！靖王转身离去。",1580,65,438,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n精英，外攻免疫，推荐内功英雄。"},
key10001415={10001415,10041,5,"挽救",1,1,0,10001414,0,1,4,6,8305,"梅长苏始料未及，赶忙奔往靖王府，请见靖王，靖王却不见。",654,65,66,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001416={10001416,10041,6,"寒冬",1,1,0,10001415,0,1,4,6,8306,"寒风凛冽，直入骨髓，梅长苏挺立在靖王府前，岿然不动。",655,65,902,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001417={10001417,10041,7,"痛斥",1,1,0,10001416,0,1,4,6,8307,"半个时辰后，靖王出面让他回去，梅长苏急怒之下痛斥靖王。",656,65,902,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001418={10001418,10041,8,"清醒",3,1,0,10001417,0,1,4,6,8308,"萧景琰，你有情有义，你怎么就没脑子！一句话止住靖王回府的步伐。",658,65,66,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\nBOSS，外攻免疫，推荐内功英雄。"},
key10001421={10001421,10042,1,"重归于好",1,1,0,10001418,0,1,4,6,8401,"梅长苏痛斥之下靖王恢复了一些理智，软化下来与其商谈对策。",657,67,902,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001422={10001422,10042,2,"巨盗横行",1,1,0,10001421,0,1,4,6,8402,"不久后，数名流窜于外州府的巨盗趁着年节潜入京城，洗掳高官府邸。",652,67,775,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001423={10001423,10042,3,"巡防戒严",1,1,0,10001422,0,1,4,6,8403,"梁帝大怒，靖王表示会倾力严查，并出动巡防营满城戒严。",653,67,66,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n普通的敌人。"},
key10001424={10001424,10042,4,"疑窦丛生",2,1,0,10001423,0,1,4,6,8404,"这番动作在夏江眼中等于借缉盗之名准备营救，有疑兵之策，仍需援手。",1580,67,797,"1014001,10;1012001,5;1,20","1,100;2,1580","推荐英雄等级：66~67\n精英，内攻免疫，推荐外功英雄。"},
key10001425={10001425,10042,5,"会见言侯",1,1,0,10001424,0,1,4,6,8405,"梅长苏登门拜访言侯，与言豫津三人一室内谈话。",654,67,429,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：66~67\n普通的敌人。"},
key10001426={10001426,10042,6,"请求帮助",1,1,0,10001425,0,1,4,6,8406,"梅长苏直言靖王将营救卫峥，希望言侯能助靖王一臂之力。",655,67,902,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：66~67\n普通的敌人。"},
key10001427={10001427,10042,7,"情与义",1,1,0,10001426,0,1,4,6,8407,"言侯诧异，但思虑片刻，便同意帮助靖王与梅长苏。",656,67,429,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：66~67\n普通的敌人。"},
key10001428={10001428,10042,8,"立于世",3,1,0,10001427,0,1,4,6,8408,"如此愚蠢，又如此有胆魄的人，已经很久没有见到了。",658,67,774,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：66~67\nBOSS，外攻免疫，推荐内功英雄。"},
key10001431={10001431,10043,1,"纪王爷",1,1,0,10001428,0,1,4,6,8101,"言豫津依计来到纪王府，和纪王酒盏之间假装无意提起宫羽之事。",657,67,430,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001432={10001432,10043,2,"难解曲瘾",1,1,0,10001431,0,1,4,6,8102,"纪王果然来了兴趣，要言豫津带他去听宫羽姑娘的新曲。",652,67,401,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001433={10001433,10043,3,"相约听曲",1,1,0,10001432,0,1,4,6,8103,"言豫津与纪王相约初五去登甲巷一起听宫羽姑娘弹曲。",653,67,430,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001434={10001434,10043,4,"筹划营救",2,1,0,10001433,0,1,4,6,8104,"另一边苏宅，梅长苏为药王谷高手们布置初五营救卫峥的行动方案。",1580,67,1490,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n精英，内攻免疫，推荐外功英雄。"},
key10001435={10001435,10043,5,"初五",1,1,0,10001434,0,1,4,6,8105,"初五清早，夏冬同往年一样出城祭扫亡夫陵墓。",654,67,419,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001436={10001436,10043,6,"引开夏秋",1,1,0,10001435,0,1,4,6,8106,"夏冬出城时，以没空提审犯人为由，支开了兄长夏秋。",655,67,420,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001437={10001437,10043,7,"夏江之谋",1,1,0,10001436,0,1,4,6,8107,"夏江也通过秦般弱传话，让誉王今日务必找个缘由进宫陪在梁帝身边。",656,67,1114,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\n普通的敌人。"},
key10001438={10001438,10043,8,"言阙之邀",3,1,0,10001437,0,1,4,6,8108,"同时夏江带着夏春疾驰上路，言侯约他今日到城外西郊寒钟观一会。",658,67,793,"1014001,10;1012001,5;1,20","1,100;2,1600","推荐英雄等级：67~68\nBOSS，外攻免疫，推荐内功英雄。"},
key10001441={10001441,10044,1,"旧时印象",1,1,0,10001438,0,1,4,6,8201,"夏江如约而至，二人坐下相谈，言阙难掩对他冷血无情的鄙视。",657,67,429,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001442={10001442,10044,2,"冷血无情",1,1,0,10001441,0,1,4,6,8202,"言阙质问在点燃火药之时是否考虑过夏冬，夏江冷笑道夏冬背叛在先。",652,67,1482,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001443={10001443,10044,3,"凶狠之言",1,1,0,10001442,0,1,4,6,8203,"言阙斥责夏江卑劣冷血，夏江则告诉言阙这次他绝对让靖王难脱干系。",653,67,66,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001444={10001444,10044,4,"落入陷阱",2,1,0,10001443,0,1,4,6,8204,"言阙起身，告诉夏江自己可以走了。夏江觉察不对，飞奔回城。",1580,67,792,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n精英，外攻免疫，推荐内功英雄。"},
key10001445={10001445,10044,5,"巡防营之乱",1,1,0,10001444,0,1,4,6,8205,"夏江赶回悬镜司，悬镜司守卫禀告了劫匪和靖王巡防营的怪异举动。",654,67,1482,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001446={10001446,10044,6,"自乱阵脚",1,1,0,10001445,0,1,4,6,8206,"夏春惊呼，难道劫匪已经找到了关押卫峥的真正地点?夏江大惊。",655,67,481,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001447={10001447,10044,7,"露出破绽",1,1,0,10001446,0,1,4,6,8207,"夏江匆忙赶往大理寺，却发现并无异常。夏江这才反应过来。",656,67,1482,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\n普通的敌人。"},
key10001448={10001448,10044,8,"营救成功",3,1,0,10001447,0,1,4,6,8208,"但为时已晚，尾行的江左盟众人成功尾随突入，劫走了卫峥。",658,67,1482,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：67~68\nBOSS，外攻免疫，推荐内功英雄。"},
key10001451={10001451,10045,1,"大功告成",1,1,0,10001448,0,1,4,6,8301,"成功的消息传入靖王府，靖王与梅长苏激动不已。",657,69,66,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：68~69\n普通的敌人。"},
key10001452={10001452,10045,2,"未完之事",1,1,0,10001451,0,1,4,6,8302,"梅长苏料定夏江一定会入宫禀告皇帝，让靖王做好准备。",652,69,902,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：68~69\n普通的敌人。"},
key10001453={10001453,10045,3,"心有余言",1,1,0,10001452,0,1,4,6,8303,"靖王称是，梅长苏欲言又止，靖王追问，却敷衍而过。",653,69,66,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：68~69\n普通的敌人。"},
key10001454={10001454,10045,4,"进宫",2,1,0,10001453,0,1,4,6,8304,"靖王起身离开，很快便被宣召进宫了，梅长苏眉间仍有忧虑。",1580,69,774,"1014001,10;1012001,5;1,20","1,100;2,1620","推荐英雄等级：68~69\n精英，外攻免疫，推荐内功英雄。"},
key10001455={10001455,10045,5,"沉稳应对",1,1,0,10001454,0,1,4,6,8305,"靖王入宫，梁帝严厉质问，靖王对答如流，还反问夏江大理寺的疑点。",654,69,1482,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001456={10001456,10045,6,"悖逆之事",1,1,0,10001455,0,1,4,6,8306,"此时在誉王安排下，皇后在静妃宫内查出宸妃灵位，向梁帝禀告。",655,69,1114,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001457={10001457,10045,7,"解局",1,1,0,10001456,0,1,4,6,8307,"梁帝怒气冲冲来到芷萝宫，发现静妃冤屈，大事化小，皇后不忿。",656,69,402,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001458={10001458,10045,8,"神思不宁",3,1,0,10001457,0,1,4,6,8308,"此时苏宅，飞流采来花，梅长苏喜爱不已，却又吩咐黎刚拿出护心丹。",658,69,420,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\nBOSS，外攻免疫，推荐内功英雄。"},
key10001461={10001461,10046,1,"提审苏哲",1,1,0,10001458,0,1,4,6,8401,"梁帝回到殿上，夏江见势不好，提出要提审江左盟宗主梅长苏。",657,69,1482,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001462={10001462,10046,2,"无力回天",1,1,0,10001461,0,1,4,6,8402,"靖王极力补救，可梁帝最终还是同意，靖王方才明白梅长苏支吾之意。",652,69,902,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001463={10001463,10046,3,"意外",1,1,0,10001462,0,1,4,6,8403,"夏江抓捕了梅长苏，令他招供，不料梅长苏却老老实实地全招了。",653,69,902,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001464={10001464,10046,4,"卫峥下落",2,1,0,10001463,0,1,4,6,8404,"夏江又逼问梅长苏卫峥下落，梅长苏仔仔细细告知夏江卫峥早已出城。",1580,69,416,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n精英，内攻免疫，推荐外功英雄。"},
key10001465={10001465,10046,5,"登甲巷",1,1,0,10001464,0,1,4,6,8405,"另一边，纪王在宫羽的小屋里见到了夏冬押解一个犯人出城。",654,69,419,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001466={10001466,10046,6,"推波助澜",1,1,0,10001465,0,1,4,6,8406,"纪王在言豫津暗示下发现此事非同小可，决定进宫面圣。",655,69,430,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001467={10001467,10046,7,"夏冬",1,1,0,10001466,0,1,4,6,8407,"梁帝闻言大怒，命蒙挚到悬镜司提审夏冬，不得告知夏江。",656,69,419,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\n普通的敌人。"},
key10001468={10001468,10046,8,"乌金丸",3,1,0,10001467,0,1,4,6,8408,"悬镜司中梅长苏对答如流，夏江拿出了毒药乌金丸威胁他不得乱说。",658,69,1482,"1014001,10;1012001,5;1,20","1,100;2,1640","推荐英雄等级：68~69\nBOSS，外攻免疫，推荐内功英雄。"},
key10001471={10001471,10047,1,"亡夫之痛",1,1,0,10001468,0,1,4,6,8101,"夏冬带到，承认去过纪王所说的陋巷，一口咬定是为了复仇。",657,69,419,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001472={10001472,10047,2,"猜疑",1,1,0,10001471,0,1,4,6,8102,"夏冬谢罪，称一切都是自己所为，与夏江没有干系，成功引出猜忌。",652,69,1482,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001473={10001473,10047,3,"查封悬镜司",1,1,0,10001472,0,1,4,6,8103,"梁帝大怒，下令查封悬镜司，并下旨将夏江和夏冬直接投入天牢。",653,69,419,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001474={10001474,10047,4,"先下手为强",2,1,0,10001473,0,1,4,6,8104,"悬镜司中，夏江听闻夏冬被捕，深感不妙，令夏春解决梅长苏。",1580,69,1129,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n精英，外攻免疫，推荐内功英雄。"},
key10001475={10001475,10047,5,"绝境",1,1,0,10001474,0,1,4,6,8105,"誉王得知悬镜司被查，自己肯定也脱不了干系，一时间没了主意。",654,69,1114,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001476={10001476,10047,6,"私炮坊",1,1,0,10001475,0,1,4,6,8106,"蔡荃和沈追请求觐见，并向梁帝告知当年私炮房案件与誉王有关。",655,69,1114,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001477={10001477,10047,7,"宽恕",1,1,0,10001476,0,1,4,6,8107,"誉王进宫便向梁帝喊冤，梁帝思虑再三，最终决定令蔡荃沈追结案。",656,69,1109,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001478={10001478,10047,8,"七日",3,1,0,10001477,0,1,4,6,8108,"梅长苏与夏春周旋，拖来救兵，夏江提醒梅长苏只有七日。",658,69,1482,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\nBOSS，外攻免疫，推荐内功英雄。"},
key10001481={10001481,10048,1,"耿直之臣",1,1,0,10001478,0,1,4,6,8201,"沈追府上，蔡荃想起殿上种种，心中愤慨，借酒消愁。",657,71,1109,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001482={10001482,10048,2,"芷萝宫",1,1,0,10001481,0,1,4,6,8202,"芷罗宫内，静妃恳求梁帝不要对靖王太过恩宠，梁帝则叫静妃宽心。",652,71,66,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001483={10001483,10048,3,"办事王爷",1,1,0,10001482,0,1,4,6,8203,"梁帝提及储位之争，暗示靖王可以参与东宫角逐，静妃假意踌躇。",653,71,66,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n普通的敌人。"},
key10001484={10001484,10048,4,"誉王之祸",2,1,0,10001483,0,1,4,6,8204,"誉王回府暗自神伤，感慨成王败寇自有定数，自己今后已与皇位无缘。",1580,71,774,"1014001,10;1012001,5;1,20","1,100;2,1660","推荐英雄等级：69~70\n精英，外攻免疫，推荐内功英雄。"},
key10001485={10001485,10048,5,"探询",1,1,0,10001484,0,1,4,6,8205,"静妃佯装好奇向梁帝询问梅长苏，感慨梅长苏替靖王遭受无妄之灾。",654,71,902,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：69~70\n普通的敌人。"},
key10001486={10001486,10048,6,"春猎之允",1,1,0,10001485,0,1,4,6,8206,"梁帝允诺三月春猎将带静妃同行，还可让她届时召见梅长苏。",655,71,902,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：69~70\n普通的敌人。"},
key10001487={10001487,10048,7,"真相大白",1,1,0,10001486,0,1,4,6,8207,"靖王进宫探望静妃，静妃令小新说出当时被困的真相，靖王震动。",656,71,66,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：69~70\n普通的敌人。"},
key10001488={10001488,10048,8,"夏冬之志",3,1,0,10001487,0,1,4,6,8208,"蒙挚探望夏冬，二人立誓要还逝者公道，期间夏冬提及乌金丸之毒。",658,71,1109,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：69~70\nBOSS，外攻免疫，推荐内功英雄。"},
key10001491={10001491,10049,1,"宗主病重",1,1,0,10001488,0,1,4,6,8301,"靖王冒雪来到苏宅，却得到梅长苏病重的消息。",657,71,902,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001492={10001492,10049,2,"乌金丸",1,1,0,10001491,0,1,4,6,8302,"蒙挚赶来，告诉大家梅长苏被夏江逼服过乌金丸。",652,71,1482,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001493={10001493,10049,3,"最后的机会",1,1,0,10001492,0,1,4,6,8303,"秦般弱乔装入狱向夏江求教，夏江告诉她三月春猎是最后的机会。",653,71,413,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001494={10001494,10049,4,"天牢逼问",2,1,0,10001493,0,1,4,6,8304,"靖王与蒙挚大急，赶到天牢，令夏江交出乌金丸解药。",1580,71,775,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n精英，内攻免疫，推荐外功英雄。"},
key10001495={10001495,10049,5,"毒药",1,1,0,10001494,0,1,4,6,8305,"夏江看着着急的靖王二人，十分得意，告知二人乌金之毒，无人能解。",654,71,1482,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001496={10001496,10049,6,"决心",1,1,0,10001495,0,1,4,6,8306,"靖王想起小殊心中难过，下定决心一定要为已故之人沉冤昭雪。",655,71,1110,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001497={10001497,10049,7,"以毒攻毒",1,1,0,10001496,0,1,4,6,8307,"梅长苏的火寒毒化解了乌金丸的毒性，大家松了一口气。",656,71,902,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\n普通的敌人。"},
key10001498={10001498,10049,8,"挺过来了",3,1,0,10001497,0,1,4,6,8308,"梅长苏挺过悬镜司的毒辣手段，刚下病榻便叮嘱甄平盯紧被捕的童路。",658,71,1489,"1014001,10;1012001,5;1,20","1,100;2,1680","推荐英雄等级：70~71\nBOSS，外攻免疫，推荐内功英雄。"},
key10001501={10001501,10050,1,"欲拒还迎",1,1,0,10001498,0,1,4,6,8401,"言皇后对静妃和惠妃百般刁难，梁帝安抚静妃，并欲提升她的位份。",657,71,402,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001502={10001502,10050,2,"惠妃",1,1,0,10001501,0,1,4,6,8402,"静妃提起惠妃被殃及，梁帝决定让惠妃之子景庭审理悬镜司一案。",652,71,1481,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001503={10001503,10050,3,"失帝心",1,1,0,10001502,0,1,4,6,8403,"经过悬镜司一案，梁帝彻底对夏江失去了信任，对誉王也颇为不满。",653,71,1114,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001504={10001504,10050,4,"大势已去",2,1,0,10001503,0,1,4,6,8404,"蒙挚步步紧逼，夏江手中砝码全部失算，终于明白自己大势已去。",1580,71,1488,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n精英，内攻免疫，推荐外功英雄。"},
key10001505={10001505,10050,5,"重整旗鼓",1,1,0,10001504,0,1,4,6,8405,"誉王府中，秦般弱苦劝誉王重振旗鼓。",654,71,413,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001506={10001506,10050,6,"不愿争斗",1,1,0,10001505,0,1,4,6,8406,"被命幽闭府中的誉王颓废消沉，感慨不想再争斗权利之事。",655,71,1114,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001507={10001507,10050,7,"锦囊",1,1,0,10001506,0,1,4,6,8407,"秦般弱乔装打扮潜入狱中，夏江提起璇玑公主留给她的锦囊。",656,71,426,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\n普通的敌人。"},
key10001508={10001508,10050,8,"玲珑公主",3,1,0,10001507,0,1,4,6,8408,"两人惊讶的发现誉王原来竟是梁帝与滑族玲珑公主所生之子。",658,71,413,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：70~71\nBOSS，内攻免疫，推荐外功英雄。"},
key10001511={10001511,10051,1,"滑族血脉",1,1,0,10001508,0,1,4,6,8101,"秦般弱赶回誉王府，将书信交予誉王，并告知他为玲珑之子。",657,73,426,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：71~72\n普通的敌人。"},
key10001512={10001512,10051,2,"策划谋反",1,1,0,10001511,0,1,4,6,8102,"誉王将累积的愤恨都对准了梁帝，决定在三月春围时谋划策反。",652,73,1114,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：71~72\n普通的敌人。"},
key10001513={10001513,10051,3,"卫峥相认",1,1,0,10001512,0,1,4,6,8103,"卫峥终于与梅长苏相认相见，梅长苏强调不可将自己是林殊暴露给靖王。",653,73,418,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：71~72\n普通的敌人。"},
key10001514={10001514,10051,4,"终知真相",2,1,0,10001513,0,1,4,6,8104,"靖王趁夜通过密道赶到雪庐，亲耳听卫峥诉说了当年真相。",1580,73,797,"1014001,10;1012001,5;1,20","1,100;2,1700","推荐英雄等级：71~72\n精英，内攻免疫，推荐外功英雄。"},
key10001515={10001515,10051,5,"无耻陷害",1,1,0,10001514,0,1,4,6,8105,"原来十三年前赤焰军击败大渝军队后在梅岭遭谢玉陷害。",654,73,424,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001516={10001516,10051,6,"悲愤交集",1,1,0,10001515,0,1,4,6,8106,"靖王终于得以知道当年实情，悲愤交加。",655,73,66,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001517={10001517,10051,7,"火寒毒",1,1,0,10001516,0,1,4,6,8107,"秦般弱利用隽娘骗诱童路，得知了梅长苏曾中过火寒毒。",656,73,413,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001518={10001518,10051,8,"静妃怀疑",3,1,0,10001517,0,1,4,6,8108,"三月春猎，梁帝携静妃前往九安山，静妃要靖王带梅长苏前来见面。",658,73,424,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\nBOSS，内攻免疫，推荐外功英雄。"},
key10001521={10001521,10052,1,"金陵被控",1,1,0,10001518,0,1,4,6,8201,"金陵城中，誉王成功说服皇后协助自己的谋逆之举。",657,73,402,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001522={10001522,10052,2,"切脉",1,1,0,10001521,0,1,4,6,8202,"静妃为梅长苏切脉，探得其病状后忍不住有些失态。",652,73,902,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001523={10001523,10052,3,"痛哭",1,1,0,10001522,0,1,4,6,8203,"静妃强行将靖王请出帐外，终于控制不住自己的情绪失声痛哭。",653,73,66,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001524={10001524,10052,4,"反常",2,1,0,10001523,0,1,4,6,8204,"母亲的反常让靖王满腹疑虑并询问梅长苏所出何因，梅长苏闭口不提。",1580,73,1488,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n精英，内攻免疫，推荐外功英雄。"},
key10001525={10001525,10052,5,"背后的宫羽",1,1,0,10001524,0,1,4,6,8205,"梅长苏发现了混进禁军队伍里默默跟着自己的宫羽。",654,73,401,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001526={10001526,10052,6,"隽娘的消息",1,1,0,10001525,0,1,4,6,8206,"隽娘听闻誉王谋反，舍命将童路救出带出消息。",655,73,68,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001527={10001527,10052,7,"童路之死",1,1,0,10001526,0,1,4,6,8207,"童路告知黎刚与甄平，自己却在送信的路上为掩护甄平而死。",656,73,418,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\n普通的敌人。"},
key10001528={10001528,10052,8,"杀向猎宫",3,1,0,10001527,0,1,4,6,8208,"誉王联合庆历军一道向猎宫杀来，",658,73,1490,"1014001,10;1012001,5;1,20","1,100;2,1720","推荐英雄等级：71~72\nBOSS，内攻免疫，推荐外功英雄。"},
key10001531={10001531,10053,1,"暗查火寒毒",1,1,0,10001528,0,1,4,6,8301,"夏江与秦般弱趁夜潜入悬镜司暗衙，查询梅长苏所中火寒毒病症。",657,73,902,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001532={10001532,10053,2,"主动出击",1,1,0,10001531,0,1,4,6,8302,"誉王派兵攻击九安山，蒙挚率兵主动回击，令誉王先行部队损失惨重。",652,73,69,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001533={10001533,10053,3,"一举包抄",1,1,0,10001532,0,1,4,6,8303,"梅长苏看穿了誉王想要一举包抄的布兵策略，派蒙挚带兵与其周旋。",653,73,69,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001534={10001534,10053,4,"扑空",2,1,0,10001533,0,1,4,6,8304,"梅长苏布置陷阱，使得趁夜潜入围猎大营的庆历军先行部队扑了个空。",1580,73,413,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n精英，内攻免疫，推荐外功英雄。"},
key10001535={10001535,10053,5,"死守",1,1,0,10001534,0,1,4,6,8305,"既已无路可退，众人下定决心死守猎宫等待靖王，不到最后一刻绝不倒下。",654,73,66,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001536={10001536,10053,6,"步步逼来",1,1,0,10001535,0,1,4,6,8306,"庆历军步步逼来，守卫军将城门紧闭，在城墙之上与谋反军队拼死交战。",655,73,69,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001537={10001537,10053,7,"城门破",1,1,0,10001536,0,1,4,6,8307,"庆历军倚仗人多，持续强攻，猎宫城门洞开，猎宫眼看即将不保。",656,73,1114,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001538={10001538,10053,8,"誓死守卫",3,1,0,10001537,0,1,4,6,8308,"殿内，众人表示绝不会屈于誉王，梁帝深受感染，也持剑与大家站在一起。",658,73,1114,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\nBOSS，外攻免疫，推荐内功英雄。"},
key10001541={10001541,10054,1,"捕获怪兽",1,1,0,10001538,0,1,4,6,8401,"抓获怪兽，梅长苏执意向靖王要求将怪兽带回身边诊治。",657,75,902,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001542={10001542,10054,2,"毛人真相",1,1,0,10001541,0,1,4,6,8402,"梅长苏通过毛人的手环肯定他是当年旧将聂锋，并知其也中火寒毒。",652,75,412,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001543={10001543,10054,3,"无力根治",1,1,0,10001542,0,1,4,6,8403,"两人相认抱头痛哭，梅长苏请来静妃诊治，静妃表示自己无力根治。",653,75,902,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n普通的敌人。"},
key10001544={10001544,10054,4,"旧病复发",2,1,0,10001543,0,1,4,6,8404,"梅长苏将最后一粒药给了聂锋，自己却旧病复发。",1580,75,1489,"1014001,10;1012001,5;1,20","1,100;2,1740","推荐英雄等级：72~73\n精英，外攻免疫，推荐内功英雄。"},
key10001545={10001545,10054,5,"梅石楠",1,1,0,10001544,0,1,4,6,8405,"靖王前来探望并询问其父亲的名讳，梅长苏沉吟家父为梅石楠。",654,75,902,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：72~73\n普通的敌人。"},
key10001546={10001546,10054,6,"失控",1,1,0,10001545,0,1,4,6,8406,"靖王随即询问静妃梅长苏家父名讳，静妃给出一样的答案，靖王失控。",655,75,66,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：72~73\n普通的敌人。"},
key10001547={10001547,10054,7,"夏江逃狱",1,1,0,10001546,0,1,4,6,8407,"蒙挚收服了金陵禁军，夏江逃出了监牢，梁帝下令无论死活捉拿归案。",656,75,1482,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：72~73\n普通的敌人。"},
key10001548={10001548,10054,8,"誉王抄家",3,1,0,10001547,0,1,4,6,8408,"梁帝回京，誉王及誉王府上下都被投入大狱。",658,75,1129,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：72~73\nBOSS，外攻免疫，推荐内功英雄。"},
key10001551={10001551,10055,1,"宫羽换夏冬",1,1,0,10001548,0,1,4,6,8101,"宫羽主动请缨，称自己略懂易容术，可以潜入大牢将夏冬换出。",657,75,401,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001552={10001552,10055,2,"谢玉身死",1,1,0,10001551,0,1,4,6,8102,"靖王众望所归将封太子，密探来报谢玉在流放途中意外死亡。",652,75,424,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001553={10001553,10055,3,"太子监国",1,1,0,10001552,0,1,4,6,8103,"靖王册立东宫太子，梁帝身体渐不如前，遂令太子监国理政。",653,75,66,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001554={10001554,10055,4,"夫妻相见",2,1,0,10001553,0,1,4,6,8104,"夏冬在众人掩护下见到聂锋，夫妻两人执手默默垂泪。",1580,75,1726,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n精英，外攻免疫，推荐内功英雄。"},
key10001555={10001555,10055,5,"苦难",1,1,0,10001554,0,1,4,6,8105,"蔺晨火寒毒症状描述，众人这才知道梅长苏经历了怎样的苦难。",654,75,406,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001556={10001556,10055,6,"十年之约",1,1,0,10001555,0,1,4,6,8106,"梅长苏对蒙挚和霓凰逐一安抚，谎称自己还有十年寿命。",655,75,902,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001557={10001557,10055,7,"是林殊！",1,1,0,10001556,0,1,4,6,8107,"梅长苏发病次数越密，同时梁帝却被越贵妃告知梅长苏是林殊。",656,75,1110,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\n普通的敌人。"},
key10001558={10001558,10055,8,"梁帝的怀疑",3,1,0,10001557,0,1,4,6,8108,"梁帝命御书院查火寒毒，同时传话夏江，若口供属实罪责全免。",658,75,1726,"1014001,10;1012001,5;1,20","1,100;2,1760","推荐英雄等级：73~74\nBOSS，外攻免疫，推荐内功英雄。"},
key10001561={10001561,10056,1,"铺好后路",1,1,0,10001558,0,1,4,6,8201,"夏江决定进宫面见皇上，临行前嘱咐手下后路如何。",657,75,1482,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001562={10001562,10056,2,"召入宫中",1,1,0,10001561,0,1,4,6,8202,"御书院找到火寒毒的记载，梁帝疑心加重，派人将其召至宫中。",652,75,71,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001563={10001563,10056,3,"钳制禁军",1,1,0,10001562,0,1,4,6,8203,"同时，御林军火速赶到蒙挚的禁军统领府钳制其活动。",653,75,69,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001564={10001564,10056,4,"我就是林殊",2,1,0,10001563,0,1,4,6,8204,"梅长苏，靖王，夏江对峙御前，梅长苏淡然承认自己便是林殊。",1580,75,756,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n精英，内攻免疫，推荐外功英雄。"},
key10001565={10001565,10056,5,"午时不侯",1,1,0,10001564,0,1,4,6,8205,"宫外，霓凰与蒙挚集结兵力，若午时没消息便冲入宫中。",654,75,798,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001566={10001566,10056,6,"错杀不错放",1,1,0,10001565,0,1,4,6,8206,"夏江情绪激动引发梁帝怀疑，夏江盛怒大呼宁可错杀不可错放。",655,75,1482,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001567={10001567,10056,7,"毒酒",1,1,0,10001566,0,1,4,6,8207,"梁帝心疑，命高湛准备御酒，其中一杯是为梅长苏准备的毒酒。",656,75,902,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\n普通的敌人。"},
key10001568={10001568,10056,8,"离开养居殿",3,1,0,10001567,0,1,4,6,8208,"景琰将毒酒拦下，景琰将酒缓缓倒掉，携梅长苏走出养居殿。",658,75,1726,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：73~74\nBOSS，外攻免疫，推荐内功英雄。"},
key10001571={10001571,10057,1,"密报",1,1,0,10001568,0,1,4,6,8301,"蔡荃收到密报，发现夏冬在牢中被蒙挚掉包。",657,75,1109,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：74~75\n普通的敌人。"},
key10001572={10001572,10057,2,"兵不血刃",1,1,0,10001571,0,1,4,6,8302,"蔺晨想了一个兵不血刃便的办法使得蔡荃对此事不再追究。",652,75,406,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：74~75\n普通的敌人。"},
key10001573={10001573,10057,3,"新朝",1,1,0,10001572,0,1,4,6,8303,"兵部驻军换防方案得到太子肯定，蔡荃直言新朝应有新气象。",653,75,1109,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：74~75\n普通的敌人。"},
key10001574={10001574,10057,4,"说话小心",2,1,0,10001573,0,1,4,6,8304,"景琰正色到哪里有什么新朝，让他说话小心。",1580,75,1726,"1014001,10;1012001,5;1,20","1,100;2,1780","推荐英雄等级：74~75\n精英，外攻免疫，推荐内功英雄。"},
key10001575={10001575,10057,5,"誉王与滑族",1,1,0,10001574,0,1,4,6,8305,"梅长苏回想起誉王死前的话语，怀疑誉王可能与滑族有关系。",654,75,426,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001576={10001576,10057,6,"滑族皇子",1,1,0,10001575,0,1,4,6,8306,"梅长苏询问宫羽才得知滑族玲珑公主曾经有过一个孩子。",655,75,1114,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001577={10001577,10057,7,"夏江夫人",1,1,0,10001576,0,1,4,6,8307,"言侯大寿，诸多达官显贵前来祝贺，夏江原夫人携子前来。",656,75,429,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001578={10001578,10057,8,"大肃清",3,1,0,10001577,0,1,4,6,8308,"韩氏列举名单展开一场大肃清，梅长苏将一切都告诉了夏江。",658,75,1490,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\nBOSS，内攻免疫，推荐外功英雄。"},
key10001581={10001581,10058,1,"行刺",1,1,0,10001578,0,1,4,6,8401,"夏江手下行刺莅阳长公主，萧景睿及时赶到，刺客未能得逞。",657,75,423,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001582={10001582,10058,2,"不复从前",1,1,0,10001581,0,1,4,6,8402,"梅长苏告知景琰自己不能恢复林殊身份，景琰含泪答应。",652,75,1110,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001583={10001583,10058,3,"谢玉手书",1,1,0,10001582,0,1,4,6,8403,"莅阳不解是何人要将她置死，母子二人决定一起查看手书内容。",653,75,400,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001584={10001584,10058,4,"翻案",2,1,0,10001583,0,1,4,6,8404,"得知了当年秘密，莅阳公主在景睿和梅长苏劝说下答应入宫翻案。",1580,75,775,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n精英，内攻免疫，推荐外功英雄。"},
key10001585={10001585,10058,5,"梁帝寿辰",1,1,0,10001584,0,1,4,6,8405,"梁帝寿宴群臣祝贺，莅阳长公主却跪求梁帝重审赤焰旧案。",654,75,400,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001586={10001586,10058,6,"百官叩首",1,1,0,10001585,0,1,4,6,8406,"龙颜大怒，然而文武百官甚至纪王也叩请梁帝答应重审赤焰旧案。",655,75,429,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001587={10001587,10058,7,"不可有林殊",1,1,0,10001586,0,1,4,6,8407,"梁帝无言以对，答应旧案可重审，但林殊决不可再活在朝堂之上。",656,75,1110,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\n普通的敌人。"},
key10001588={10001588,10058,8,"赤焰案终",3,1,0,10001587,0,1,4,6,8408,"赤焰一案终得告结，前方却战事烽烟乍起无将可用。",658,75,1782,"1014001,10;1012001,5;1,20","1,100;2,1800","推荐英雄等级：74~75\nBOSS，内攻免疫，推荐外功英雄。"},
